  align-items: stretch;
  margin-top: 10px;
  margin-bottom: 10px;
  padding: 15px;
`;

const DappContentWrapper = styled(Column)`
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #666666;
`;

