import React, { Suspense, Component } from 'react'
import { HashRouter, Route, Switch } from 'react-router-dom'
// import { BrowserHistory } from 'react-router'
import styled from 'styled-components'
import { isMobile } from 'react-device-detect'

import Header from '../components/Header'
import Assets from './Assets'
import AssetsM from './AssetsM'
import AssetsMWeb from './AssetsMWeb'
import AssetsMDetails from './AssetsMDetails'
import AssetsMDetailsWeb from './AssetsMDetailsWeb'
import DappM from './DappM'
import DappMWeb from './DappMWeb'
import SearchComponent from './Search'
import MoreDAppsComponent from './MoreDApps'
import MoreDAppsWebComponent from './MoreDAppsWeb'
import DAppsDetailComponent from './DappsDetail'
import DAppsDetailWebComponent from './DappsDetailWeb'
import CloverLogo from '../assets/images/clover_logo.svg';

const AppWrapper = styled.div`
  display: flex;
  flex-flow: column;
  justify-content: flex-start;
  align-items: stretch;
  min-height: 350px;
  overflow: auto;
`;

const BodyWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  align-items: center;

  flex: 1;
  overflow-y: auto;
  overflow-x: hidden;

  z-index: 1;
`;

export default class App extends Component<any, any> {
  constructor(props: any, context: any) {
    super(props, context);
    this.state = {
      showMobile: isMobile
    }
  }

  componentDidMount() {
    const that = this;
    window.onresize = function () {
      if (window.innerWidth < 750) {
        that.setState({showMobile: true})
      } else {
        that.setState({showMobile: false})
      }
    }
  }

  render() {
    const { showMobile } = this.state;
    return (
        <Suspense fallback={null}>
          <HashRouter>
            <AppWrapper>
              {/*{!isMobile &&*/}
              {/*<Header />*/}
              {/*}*/}
              {(!isMobile && !showMobile) &&
                  <div style={{width: '1280px',
                    margin: '0 auto'}}>
                    <img src={CloverLogo} width='182' height='54' style={{margin: '30px 20px'}} alt=""/>
                  </div>
              }
              <BodyWrapper>
                <Switch>
                  <Route exact strict path="/" component={Assets} />
                  <Route exact strict path="/assets" component={Assets} />
                  <Route exact strict path="/assetsm" component={isMobile || showMobile ? AssetsM : AssetsMWeb} />
                  {/*<Route exact strict path="/assetsmdetails" component={AssetsMDetails} />*/}
                  <Route exact strict path="/assetsmdetails" component={isMobile || showMobile ? AssetsMDetails : AssetsMDetailsWeb} />
                  <Route exact strict path="/dappm" component={isMobile || showMobile ? DappM : DappMWeb} />
                  <Route exact strict path="/search" component={SearchComponent} />
                  <Route exact strict path="/moredapps" component={isMobile || showMobile ? MoreDAppsComponent : MoreDAppsWebComponent} />
                  <Route exact strict path="/dappsdetail" component={isMobile || showMobile ? DAppsDetailComponent : DAppsDetailWebComponent} />
                </Switch>
              </BodyWrapper>
            </AppWrapper>
          </HashRouter>
        </Suspense>
    );
  }
}
