import React, {Component} from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn, ColumnCenter } from '../../components/Column'
import Row, {RowBetween, RowEnd} from '../../components/Row'
import CloseBtn from '../../assets/images/closeBtn.svg'
import {
    ResponsiveContainer,
    PieChart,
    Pie,
    Cell,
    AreaChart,
    Area,
    BarChart,
    Bar,
    LabelList,
    XAxis,
    ReferenceLine,
    Tooltip,
    Legend,
    Sector
} from 'recharts';

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
  height: 44px;
`;

const PieChartWrapper = styled.div`
  height: 250px;
  display: flex;
  justify-content: center;
  position: relative;
`;

const LineChartWrapper = styled.div`
  margin: 0 30px;
  height: 250px;
  background: linear-gradient(180deg,#FFFFFF 0%, #FFF6FA 50%, #FFFFFF 100%);
  border-radius: 15px;
`;

const BarChartWrapper = styled.div`
  position: relative;
  margin: 20px 30px;
  height: 220px;
  background: linear-gradient(180deg, #FFFFFF 0%, #EFFBFF 50%, #FFFFFF 100%);
  border-radius: 15px;
`;

export default class AssetsMDetail extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            activeIndex: null
        }
    }
    goBack = () => {
        this.props.history.goBack();
    }
    onPieEnter = (data: any, index: number) => {
        console.log(data);
        console.log(index);
        this.setState({
            activeIndex: index,
        });
    };
    render() {
        const pieData = [
            {
                id: "Total assets",
                name: "Total assets",
                value: 75,
                color: "#FF7D30"
            },
            {
                id: "Total liabilities",
                name: "Total liabilities",
                value: 25,
                color: "#FF589D"
            },
        ];
        const pieInnerStyles = {
            root: {
                fontFamily: "consolas, sans-serif",
                textAlign: "center",
                position: "relative",
                width: 600,
                height: 600,
            },
            overlay: {
                height: 110,
                width: 110,
                borderRadius: '50%',
                display: 'flex',
                flexDirection: 'column' as 'column',
                justifyContent: 'center' as 'center',
                backgroundColor: '#ffffff',
                boxShadow: '0px 5px 10px 2px rgba(255, 125, 48, 0.15)',
                position: "absolute" as "absolute",
                zIndex: 1,
                top: '50%',
                marginTop: '-66px',
                right: '50%',
                marginRight: '-55px',
                bottom: 0,
                alignItems: "center" as "center",
            },
            number: {
                color: "#333333",
                fontSize: "15px",
                fontWeight: 'bold' as 'bold'
            },
            text: {
                color: "#999999",
                fontSize: "11px",
                fontWeight: "normal" as "normal"
            }
        };
        const renderActiveTooltipPie = (props: any) => {
            const { payload } = props;
            return (
                payload[0]? <div
                    key={payload[0].name}
                    style={{
                        color: '#999999',
                        fontSize: '11px',
                        backgroundColor: '#ffffff',
                        borderRadius: '5px',
                        padding: '3px 10px',
                        boxShadow: '0px 0px 10px 2px rgba(0, 0, 0, 0.15)',
                    }}
                >
                        <span style={{
                            display: 'inline-block',
                            marginRight: '5px',
                            width: '8px',
                            height: '8px',
                            borderRadius: '4px',
                            background: payload[0].payload.color
                        }}></span>
                    {payload[0].name}
                    <span style={{color: '#333333', marginLeft: '5px', fontSize: '13px'}}>${payload[0].value}</span>
                </div> : <div></div>
            );
        };
        const renderLegendPie = (props: any) => {
            const { payload } = props;

            return (
                <div>
                    {
                        payload.map((entry: any, index: number) => (
                            <div>
                                <span style={{
                                    display: 'inline-block',
                                    marginRight: '5px',
                                    width: '8px',
                                    height: '8px',
                                    borderRadius: '4px',
                                    background: entry.color
                                }}></span>
                                <span style={{color: '#666666', fontSize: '11px'}}>{entry.value}</span>
                            </div>
                        ))
                    }
                </div>
            );
        }
        const renderActiveShapePie = (props: any) => {
            const RADIAN = Math.PI / 180;
            const {
                cx, cy, midAngle, innerRadius, outerRadius, startAngle, endAngle, cornerRadius,
                fill, payload, percent, value,
            } = props;
            const sin = Math.sin(-RADIAN * midAngle);
            const cos = Math.cos(-RADIAN * midAngle);
            const sx = cx + (outerRadius + 5) * cos;
            const sy = cy + (outerRadius + 5) * sin;
            const mx = cx + (outerRadius + 15) * cos;
            const my = cy + (outerRadius + 15) * sin;
            const ex = mx + (cos >= 0 ? 1 : -1) * 11;
            const ey = my;
            const textAnchor = cos >= 0 ? 'start' : 'end';

            return (
                <g>
                    <text x={cx} y={cy} dy={4} textAnchor="middle" fill={fill}>{payload.name}</text>
                    <Sector
                        cx={cx}
                        cy={cy}
                        innerRadius={innerRadius + 5}
                        outerRadius={outerRadius + 5}
                        startAngle={startAngle}
                        endAngle={endAngle}
                        cornerRadius={cornerRadius}
                        fill={fill}
                    />
                    <path d={`M${sx},${sy}L${mx},${my}L${ex},${ey}`} stroke={fill} fill="none" />
                    <circle cx={ex} cy={ey} r={2} fill={fill} stroke="none" />
                    {/*<text x={ex + (cos >= 0 ? 1 : -1) * 6} y={ey-5} textAnchor={textAnchor} fill="#333333" fontSize={11}>{payload.name}: ${value}</text>*/}
                    <text x={ex + (cos >= 0 ? 1 : -1) * 6} y={ey} dy={4} textAnchor={textAnchor} fill="#999" fontSize={11}>
                        {`(Rate ${(percent * 100).toFixed(2)}%)`}
                    </text>
                </g>
            );
        };

        const lineData = [
            {
                "name": "2020.07.20",
                "Total liabilities": 100,
                "NetAssets": 200,
                "TotalAssets": 300
            },
            {
                "name": "2020.08",
                "Total liabilities": 200,
                "NetAssets": 300,
                "TotalAssets": 400
            },
            {
                "name": "2020.08.10",
                "Total liabilities": 300,
                "NetAssets": 400,
                "TotalAssets": 500
            },
            {
                "name": "2020.08.20",
                "Total liabilities": 200,
                "NetAssets": 300,
                "TotalAssets": 400
            },
            {
                "name": "2020.09",
                "Total liabilities": 100,
                "NetAssets": 200,
                "TotalAssets": 300
            },
            {
                "name": "2020.09.10",
                "Total liabilities": 300,
                "NetAssets": 400,
                "TotalAssets": 500
            },
            {
                "name": "2020.09.20",
                "Total liabilities": 400,
                "NetAssets": 500,
                "TotalAssets": 600
            },
            {
                "name": "2020.10",
                "Total liabilities": 200,
                "NetAssets": 300,
                "TotalAssets": 400
            },
            {
                "name": "2020.10.10",
                "Total liabilities": 400,
                "NetAssets": 500,
                "TotalAssets": 600
            },
            {
                "name": "2020.10.20",
                "Total liabilities": 500,
                "NetAssets": 600,
                "TotalAssets": 700
            },
            {
                "name": "2020.11",
                "Total liabilities": 800,
                "NetAssets": 700,
                "TotalAssets": 900
            },
            {
                "name": "2020.11.10",
                "Total liabilities": 400,
                "NetAssets": 500,
                "TotalAssets": 600
            }
        ]
        const renderActiveTooltipLine = (props: any) => {
            const { payload } = props;
            return (
                <div
                    style={{
                        background: 'white',
                        padding: '9px 12px',
                        opacity: '0.92',
                        width: '130px',
                        height: '140px',
                        borderRadius: '10px',
                        boxShadow: '0px 0px 5px 2px rgba(0, 0, 0, 0.05)',
                    }}
                >
                    {payload.map((point: any) => (
                        <div
                            key={point.name}
                            style={{
                                color: '#999999',
                                padding: '3px 0',
                                fontSize: '11px'
                            }}
                        >
                            <div>
                                <span style={{
                                    display: 'inline-block',
                                    marginRight: '5px',
                                    width: '8px',
                                    height: '8px',
                                    borderRadius: '4px',
                                    background: point.stroke
                                }}></span>
                                {point.name}
                            </div>
                            <span style={{color: '#333333', marginLeft: '13px', fontSize: '13px'}}>${point.value}</span>
                        </div>
                    ))}
                </div>
            );
        };

        const barInnerStyles = {
            overlay: {
                width: 115,
                height: 30,
                display: 'flex',
                flexDirection: 'row' as 'row',
                position: "absolute" as "absolute",
                top: 0,
                right: 0,
            },
            span: {
                width: '3px',
                height: '100%',
                backgroundColor: '#FF7D30',
                marginRight: '5px'
            },
            divWrapper: {
                display: 'flex',
                flexDirection: 'column' as 'column',
            },
            number: {
                color: "#333333",
                fontSize: "13px",
            },
            text: {
                color: "#999999",
                textAlign: 'center' as 'center',
                fontSize: "10px",
                lineHeight: '14px',
            }
        };
        const barData = [
            {
                name: 'CLV', earnings: 25.03, color: '#FF7D30'
            },
            {
                name: 'cETH', earnings: 20.03, color: '#FF71C5'
            },
            {
                name: 'ACA', earnings: 15.03, color: '#406AFE'
            },
            {
                name: 'aUSD', earnings: 10.03, color: '#F6883B'
            },
            {
                name: 'bUSD', earnings: -10.03, color: '#FF71C5'
            },
            {
                name: 'cUSD', earnings: -15.03, color: '#FF7D30'
            }
        ];
        const BarLabel =(props: any) =>{
            const { x, y, width, height, value } = props;
            return (
                <text x={x + width / 2} y={value > 0 ? y - 15 : y + 15} fill="#666666" fontSize={11} textAnchor="middle" dominantBaseline="middle">
                    {value > 0 ? `+$${value}` : `-$${value * -1}`}
                </text>
            )
        }

        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <Text fontSize='23px' fontWeight='bold' color='#333333'>Assets</Text>
                        <img src={CloseBtn} alt="" width='15px' height='15px' onClick={this.goBack} />
                    </HeadContainer>
                    <Text fontSize='18px' fontWeight='bold' color='#333333' marginLeft='25px'>Assets distribution</Text>
                    <PieChartWrapper>
                        <ResponsiveContainer width='100%'>
                            <PieChart>
                                <Pie
                                    activeIndex={this.state.activeIndex}
                                    activeShape={renderActiveShapePie}
                                    onClick={this.onPieEnter}
                                    data={pieData}
                                    innerRadius={78}
                                    outerRadius={90}
                                    fill="#8884d8"
                                    startAngle={90}
                                    endAngle={-270}
                                    paddingAngle={5}
                                    cornerRadius={5}
                                    dataKey="value"
                                >
                                    {
                                        pieData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.color} />)
                                    }
                                </Pie>
                                <Tooltip wrapperStyle={{zIndex: 2}} content={renderActiveTooltipPie} />
                                <Legend align={'center'} verticalAlign="bottom"  content={renderLegendPie} />
                            </PieChart>
                        </ResponsiveContainer>
                        <div style={pieInnerStyles.overlay}>
                            <span style={pieInnerStyles.number}>$234995.300</span>
                            <span style={pieInnerStyles.text}>Net assets</span>
                        </div>
                    </PieChartWrapper>

                    <Text fontSize='18px' fontWeight='bold' color='#333333' marginLeft='25px'>Assets trend</Text>
                    <LineChartWrapper>
                        <ResponsiveContainer width='100%'>
                            <AreaChart style={{width: '315px'}} data={lineData}
                                       margin={{ top: 10, right: 0, left: 0, bottom: 0 }}>
                                <defs>
                                    <linearGradient id="colorLiabilities" x1="0" y1="0" x2="0" y2="1">
                                        <stop offset="5%" stopColor="#406AFE" stopOpacity={0.3}/>
                                        <stop offset="100%" stopColor="#ffffff" stopOpacity={0}/>
                                    </linearGradient>
                                    <linearGradient id="colorNetAssets" x1="0" y1="0" x2="0" y2="1">
                                        <stop offset="5%" stopColor="#FF71C5" stopOpacity={0.3}/>
                                        <stop offset="100%" stopColor="#ffffff" stopOpacity={0}/>
                                    </linearGradient>
                                    <linearGradient id="colorTotalAssets" x1="0" y1="0" x2="0" y2="1">
                                        <stop offset="5%" stopColor="#FF7D30" stopOpacity={0.3}/>
                                        <stop offset="100%" stopColor="#ffffff" stopOpacity={0}/>
                                    </linearGradient>
                                </defs>
                                <XAxis dataKey="name" axisLine={false} tickLine={false} ticks={["2020.08", "2020.09", "2020.10", "2020.11"]} tick={{fill: '#666666', fontSize: 11}} interval={0} />
                                <Tooltip cursor={{ stroke: '#eeeeee', strokeWidth: 1 }} content={renderActiveTooltipLine} />
                                <Area activeDot={{ stroke: '#FF7D30', fill: '#ffffff', strokeWidth: 2, r: 4 }} type="monotone" dataKey="TotalAssets" stroke="#FF7D30" strokeWidth={2} fillOpacity={1} fill="url(#colorTotalAssets)" />
                                <Area activeDot={{ stroke: '#FF71C5', fill: '#ffffff', strokeWidth: 2, r: 4 }} type="monotone" dataKey="NetAssets" stroke="#FF71C5" strokeWidth={2} fillOpacity={1} fill="url(#colorNetAssets)" />
                                <Area activeDot={{ stroke: '#406AFE', fill: '#ffffff', strokeWidth: 2, r: 4 }} type="monotone" dataKey="Total liabilities" stroke="#406AFE" strokeWidth={2} fillOpacity={1} fill="url(#colorLiabilities)" />
                            </AreaChart>
                        </ResponsiveContainer>
                    </LineChartWrapper>

                    <Text fontSize='18px' fontWeight='bold' color='#333333' marginLeft='25px'>Profit and loss of assets</Text>
                    <BarChartWrapper>
                        <ResponsiveContainer width='100%'>
                            <BarChart
                                data={barData}
                                margin={{
                                    top: 60, right: 20, left: 20, bottom: 20,
                                }}
                            >
                                <XAxis dx={-12} dataKey="name" axisLine={false} tickLine={false} tick={{fill: '#666666', fontSize: 11, padding: '10px'}} interval={0} />
                                <ReferenceLine y={0} stroke="rgba(64, 106, 254, 0.12)" strokeWidth={1} />
                                <Bar dataKey="earnings" fill="#8884d8" radius={[5, 5, 0, 0]}>
                                    <LabelList
                                        dataKey="earnings"
                                        position="top"
                                        content={<BarLabel />}
                                    />
                                    {
                                        barData.map((entry) => (
                                            <Cell fill={entry.color} width={16} />
                                        ))
                                    }
                                </Bar>
                            </BarChart>
                        </ResponsiveContainer>
                        <div style={barInnerStyles.overlay}>
                            <span style={barInnerStyles.span}></span>
                            <div style={barInnerStyles.divWrapper}>
                                <span style={barInnerStyles.text}>Earnings today</span>
                                <span style={barInnerStyles.number}>$1254576.03</span>
                            </div>
                        </div>
                    </BarChartWrapper>
                </BodyWrapper>
            </AppBody>
        );
    }
}
