import React, { ReactElement } from 'react'
import styled  from 'styled-components'
import Row, { RowCenter, RowBetween, RowFit } from '../Row'
import { Text } from 'rebass'
import HeaderBackground from "../../assets/images/header_background.svg";

import CloverLogo from '../../assets/images/clover_logo.svg';
import {AutoColumn} from "../Column";

const HeaderWrapper = styled.div`
  background-color: #ffffff;
`
const HeaderContainer = styled(RowBetween)`
  height: 54px;
  width: 1240px;
  margin: 0 auto;
`
const ContentWrapper = styled.div`
  height: 379px;
  width: 100%;
  background: linear-gradient(180deg, #1E455C 0%, #153040 100%);
  padding-top: 47px;
`

const ContentContainer = styled.div`
  padding-top: 30px;
  width: 1240px;
  height: 384px;
  margin: 0 auto;
  background: url('${HeaderBackground}');
`;
const SearchContainer = styled.div`
  display: grid;
  grid-template-columns: auto 75px;
  width: 400px;
  height: 44px;
  background: #FFFFFF;
  border-radius: 67px;
  border: 2px solid #FF7D30;
`;

const SearchInput = styled.input`
  text-indent: 20px;
  outline: none;
  border: none;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 16px;
  color: #333333;
  background: transparent;

  ::placeholder {
    color: rgba(65, 72, 93, 0.5);
  }
`;

const SearchButton = styled.span`
  display: inline-block;
  width: 78px;
  height: 40px;
  line-height: 40px;
  color: #ffffff;
  font-size: 16px;
  text-align: center;
  background: #FF7D30;
  border-bottom-right-radius: 59px;
  border-top-right-radius: 59px;
  cursor: pointer;

  :hover {
    background-color: #F96710;
  }
`;
export default class Header extends Component<any, any> {

        searchDapps = () => {
        // console.log(this.state.searchStr);
    }

    changeSearchStr = (event: any) => {
        this.setState({
            searchStr: event.target.value
        })
    }
    render() {
        return (
            <HeaderWrapper>
                <HeaderContainer>
                    <RowFit>
                        <img src={CloverLogo} width='151' height='38' alt=""/>
                    </RowFit>

                    <RowFit>
                        <Row>
                            <Text fontSize={16} fontWeight='bold' color='#27C96E' marginRight={48}>Home</Text>
                            <Text fontSize={16} color='#7E96AE' marginRight={48}>Blockchain</Text>
                            <Text fontSize={16} color='#7E96AE' marginRight={48}>Tokens</Text>
                            <Text fontSize={16} color='#7E96AE'>Resources</Text>
                        </Row>
                    </RowFit>
                </HeaderContainer>
                <ContentWrapper>
                    <ContentContainer>
                        <Text fontSize={44} fontWeight='bold' color='#ffffff' textAlign='center'>The Clover Blockchain Explorer</Text>
                        <Text fontSize={23} fontWeight='lighter' color='#ffffff' textAlign='center' marginTop={10}>CloverScan is lightning fast, secure and powerful</Text>
                        {/*<Text fontSize={44} fontWeight='bold' color='#ffffff' textAlign='center'>The Clover Blockchain Explorer</Text>*/}
                        <SearchContainer>
                            <SearchInput placeholder='Search for Dapps' onChange={this.changeSearchStr} />
                            <SearchButton onClick={this.searchDapps}>GO</SearchButton>
                        </SearchContainer>
                    </ContentContainer>
                </ContentWrapper>
            </HeaderWrapper>
        )
    }

}
