import React from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, { RowBetween, RowFit, RowCenter } from '../../components/Row'
import ImgMenu from '../../assets/images/menu.svg'
import ImgSearch from '../../assets/images/search.svg'
import ImgDappExchange from '../../assets/images/polkadot_exchange.svg'
import ImgDappData from '../../assets/images/polkadot_data.svg'

const BodyWrapper = styled(AutoColumn)`
  padding-left: 20px;
  padding-right: 20px;
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
`;

const SwapButton = styled(RowCenter)`
  width: 75px;
  height: 30px;
  background: rgba(251, 130, 42, 0.1);
  border-radius: 30px;
`;

const MenuWrapper = styled.img`
  margin-left: 20px;
`;

const NavBar = styled(Row)`
  margin-top: 15px;
  align-items: flex-start;
`;

const NavLink = styled(Text)`
  margin-left: 30px !important;
  font-size: 20px;
  font-weight: 500;
  color: #41485D;
  text-decoration: none;
`;

const NavLinkActive = styled(NavLink)`
  margin-left: 0px !important;
  color: #FB822A;
`;

const NavLinkActiveContainer = styled.div`
  border-bottom: 2px solid #FB822A;
  padding-bottom: 3px
`;

const SearchContainer = styled.div`
  display: grid;
  grid-template-columns: auto 50px;

  margin: 10px 0px;
  background: #FAFAFA;
  border-radius: 36px;
`;

const SearchInput = styled.input`
  margin: 5px 0px 5px 10px;
  outline: none;
  border: none;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 16px;
  background: transparent;

  ::placeholder {
    color: rgba(65, 72, 93, 0.5);
  }
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  margin-top: 10px;
  margin-bottom: 10px;
`;

const DappContentWrapper = styled(Column)`
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 20px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 16px;
  font-weight: normal;
  color: #000000;
`;

const DappType = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #C4C4C4;
`;

export default function Assets(): React.ReactElement {

  return (
    <AppBody>
      <BodyWrapper gap={'md'}>
        <HeadContainer>
          <Text fontSize='20px' fontWeight='bold' color='#000000'>DAPPa'a'a</Text>
          <RowFit>
            <SwapButton>
              <Text fontSize='15px' fontWeight='bold' color='#FB822A'>SWAP</Text>
            </SwapButton>
            <MenuWrapper src={ImgMenu} alt='menu' />
          </RowFit>
        </HeadContainer>

        <NavBar>
          <NavLinkActiveContainer><NavLinkActive>ALL</NavLinkActive></NavLinkActiveContainer>
          <NavLink>Exchange</NavLink>
          <NavLink>Tool</NavLink>
          <NavLink>Source</NavLink>
        </NavBar>

        <SearchContainer>
          <SearchInput placeholder='Search for Dapps' />
          <img src={ImgSearch} width='50' height='50' alt='Search' />
        </SearchContainer>

        <DappContainer>
          <img src={ImgDappExchange} alt='Polkadot Exchange' />
          <DappContentWrapper>
            <Column>
              <DappName>CLV Mining</DappName>
              <DappDescription>Clover Mining Platform</DappDescription>
            </Column>
            <DappType>Polkadot / Exchange</DappType>
          </DappContentWrapper>
        </DappContainer>

        <DappContainer>
          <img src={ImgDappData} alt='Polkadot Data' />
          <DappContentWrapper>
            <Column>
              <DappName>CLV Mining</DappName>
              <DappDescription>Clover Mining Platform</DappDescription>
            </Column>
            <DappType>Polkadot / Data</DappType>
          </DappContentWrapper>
        </DappContainer>

        <DappContainer>
          <img src={ImgDappExchange} alt='Polkadot Exchange' />
          <DappContentWrapper>
            <Column>
              <DappName>CLV Mining</DappName>
              <DappDescription>Clover Mining Platform</DappDescription>
            </Column>
            <DappType>Polkadot / Exchange</DappType>
          </DappContentWrapper>
        </DappContainer>

      </BodyWrapper>
    </AppBody>
  );
}
