import React, {Component} from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn, ColumnCenter } from '../../components/Column'
import Row, {RowBetween, RowEnd} from '../../components/Row'
import CloseBtn from '../../assets/images/closeBtn.svg'
import { Line } from '@ant-design/charts';

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
  height: 44px;
`;

export default class AssetsMDetail extends Component<any, any> {

    goBack = () => {
        this.props.history.goBack();
    }
    render() {
        const data = [
            { year: '1991', value: 3 },
            { year: '1992', value: 4 },
            { year: '1993', value: 3.5 },
            { year: '1994', value: 5 },
            { year: '1995', value: 4.9 },
            { year: '1996', value: 6 },
            { year: '1997', value: 7 },
            { year: '1998', value: 9 },
            { year: '1999', value: 13 },
        ];

        const config = {
            data,
            width: 800,
            height: 400,
            autoFit: false,
            xField: 'year',
            yField: 'value',
            point: {
                size: 5,
                shape: 'diamond',
            },
            label: {
                style: {
                    fill: '#aaa',
                },
            },
        };

        const ref = useRef();

        // 导出图片
        const downloadImage = () => {
            ref.current?.downloadImage();
        };

        // 获取图表 base64 数据
        const toDataURL = () => {
            console.log(ref.current?.toDataURL());
        };
        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <Text fontSize='23px' fontWeight='bold' color='#333333'>Assets</Text>
                        <img src={CloseBtn} alt="" width='15px' height='15px' onClick={this.goBack} />
                    </HeadContainer>
                    <Text fontSize='18px' fontWeight='500' color='#333333' marginLeft='25px'>Assets distribution</Text>
                    <div>
                        <button type="button" onClick={downloadImage} style={{ marginRight: 24 }}>
                            导出图片
                        </button>
                        <button type="button" onClick={toDataURL}>
                            获取图表信息
                        </button>
                        <Line {...config} chartRef={ref} />
                    </div>
                </BodyWrapper>
            </AppBody>
        );
    }
}
