import React, { Component } from 'react';
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, { RowBetween, RowFit, RowCenter } from '../../components/Row'
import ImgDappExchange from '../../assets/images/polkadot_exchange.svg'
import Application from '../../assets/images/application.svg'
import SearchIcon from '../../assets/images/search_icon.svg'
import SwapIcon from '../../assets/images/swap_icon.svg'
import LendingIcon from '../../assets/images/lending_icon.svg'
import AggregationIcon from '../../assets/images/aggregation_icon.svg'
import AuctionIcon from '../../assets/images/auction_icon.svg'
import InsuranceIcon from '../../assets/images/insurance_icon.svg'
import TransferIcon from '../../assets/images/transfer_icon.svg'
import SwipeableViews from 'react-swipeable-views';
import Select from '@material-ui/core/Select';
import withStyles from '@material-ui/core/styles/withStyles';
import InputBase from '@material-ui/core/InputBase';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import MenuItem from '@material-ui/core/MenuItem';

const BodyWrapper = styled(AutoColumn)`
  height: calc(100vh - 50px);
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
`;

const DAppIconListTitle = styled(RowBetween)`
  margin-top: 10px;
  padding: 0 20px;
  margin-left: 5px;
`;

const DAppIconListWrapper = styled(RowBetween)`
  margin-top: 15px;
  padding: 0 20px;
`;

const DappIconWrapper = styled.div`
  text-align: center;
`;

const SwapContainer = styled.div`
  margin: 15px 20px 0;
  padding: 15px 20px;
  background: rgba(246, 136, 59, 0.08);
  border-radius: 15px;
`;

const TransferWrapper = styled(Row)`
`;

const TransferImg = styled.img`
  width: 38px;
  height: 38px;
  margin: 0 15px;
`

const SecondTitle = styled(Text)`
  font-size: 18px;
  color: #333333;
  font-weight: 600;
`;

const GrayBackground = styled.div`
  background: linear-gradient(180deg, #FFFFFF 0%, #F3f3f3 100%);
`;

const TransactionButton = styled(RowCenter)`
  width: 92px;
  height: 35px;
  background: #FF7D30;
  border-radius: 18px;
  color: #ffffff;
  margin-left: 20px;
`;

const Search = styled.img`
  margin-left: 20px;
  width: 27px;
  height: 27px;
`;

const NavBar = styled(RowBetween)`
  margin-top: 15px;
  padding: 0 20px;
  align-items: flex-start;
`;

const NavLink = styled(Text)`
  font-size: 16px;
  font-weight: 500;
  color: #41485D;
  text-decoration: none;
`;

const NavLinkActive = styled(NavLink)`
  color: #FB822A;
  padding-bottom: 3px;
  border-bottom: 2px solid #FB822A;
`;

const DappListWrapper = styled.div`
  background-color: #ffffff;
  margin: 0 20px;
  border-radius: 10px;
  // width: 370px;
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  margin-top: 10px;
  margin-bottom: 10px;
  padding: 15px;
`;

const DappContentWrapper = styled(Column)`
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #666666;
`;

export default class DappM extends Component<any, any> {
  constructor(props: any, context: any) {
    super(props, context);
    this.state = {
      tabList: [
        {tabName:"Defi",id:1},
        {tabName:"Bright",id:2},
        {tabName:"Grant",id:3},
        {tabName:"Exange",id:4},
        {tabName:"NFT",id:5}
      ],
      currentIndex: 1,
      fromCurrencyType: 'CLV',
      toCurrencyType: 'ETH',
      index: 0
    }
  }

  tabChoiced = (id: any) => {
    this.setState({
      currentIndex:id
    });
  }

  searchDapps = () => {
    this.props.history.push('/search');
  }

  moreDapps = () => {
    this.props.history.push('/moredapps');
  }

  goDappsDetail = (url: any) => {
    this.props.history.push({pathname: '/dappsdetail', state: {url: url}});
  }

  handleChangeFrom = (event: any) => {
    this.setState({
      fromCurrencyType: event.target.value
    });
  };

  handleChangeTo = (event: any) => {
    this.setState({
      toCurrencyType: event.target.value
    });
  };

  handleChange = (event: any, value: any) => {
    this.setState({
      index: value,
    });
  };

  handleChangeIndex = (index: any) => {
    this.setState({
      index,
    });
  };

  render() {
    const { fromCurrencyType, toCurrencyType, tabList, currentIndex } = this.state;
    const recommendDapps = [
      {
        icon: SwapIcon,
        appName: 'Swap'
      },
      {
        icon: LendingIcon,
        appName: 'Lending'
      },
      {
        icon: AggregationIcon,
        appName: 'Aggregation'
      },
      {
        icon: AuctionIcon,
        appName: 'Auction'
      },
      {
        icon: InsuranceIcon,
        appName: 'Insurance'
      },
    ];
    const dapplist = [
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system',
        url: 'https://swap.clover.finance/#/'
      }
    ]

    const styles = {
      tabs: {
        background: '#fff',
      },
      slide: {
        padding: 15,
        minHeight: 100,
        color: '#fff',
      },
      slide1: {
        backgroundColor: '#FEA900',
      },
      slide2: {
        backgroundColor: '#B3DC4A',
      },
      slide3: {
        backgroundColor: '#6AC0FF',
      },
    };

    const BootstrapInput = withStyles((theme) => ({
      input: {
        position: 'relative',
        border: 'none',
        fontSize: 20,
        '&:focus': {
          backgroundColor: 'rgba(246, 136, 59, 0.08)'
        },
      },
    }))(InputBase);

    const { index } = this.state;

    return (
        <AppBody>
          <BodyWrapper gap={'md'}>
            <HeadContainer>
              <img width="148" height="18" src={Application} alt=""/>
              <Search src={SearchIcon} alt='search' onClick={this.searchDapps} />
            </HeadContainer>
            <div>
              <Tabs value={index} onChange={this.handleChange} style={styles.tabs}>
                <Tab label="tab n°1" />
                <Tab label="tab n°2" />
                <Tab label="tab n°3" />
              </Tabs>
              <SwipeableViews index={index} onChangeIndex={this.handleChangeIndex}>
                <div style={Object.assign({}, styles.slide, styles.slide1)}>slide n°1</div>
                <div style={Object.assign({}, styles.slide, styles.slide2)}>
                  slide n°2
                  <Select value={10} autoWidth={false}>
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value={10}>Ten</MenuItem>
                  </Select>
                </div>
                <div style={Object.assign({}, styles.slide, styles.slide3)}>slide n°3</div>
              </SwipeableViews>
            </div>
            <div>
              <DAppIconListTitle>
                <SecondTitle>My DApps</SecondTitle>
                <Text color='#333333' fontSize='14px' onClick={this.moreDapps}>more</Text>
              </DAppIconListTitle>
              <DAppIconListWrapper>
                {recommendDapps.map((dapp) => (
                    <DappIconWrapper>
                      <img width='50' height='50' src={dapp.icon} alt="" />
                      <Text fontSize='12px' color='#666666' marginTop='5px'>{dapp.appName}</Text>
                    </DappIconWrapper>
                ))}
              </DAppIconListWrapper>
            </div>
            <div>
              <DAppIconListTitle>
                <SecondTitle>Recommend DApps</SecondTitle>
              </DAppIconListTitle>
              <DAppIconListWrapper>
                {recommendDapps.map((dapp) => (
                    <DappIconWrapper>
                      <img width='50' height='50' src={dapp.icon} alt="" />
                      <Text fontSize='12px' color='#666666' marginTop='5px'>{dapp.appName}</Text>
                    </DappIconWrapper>
                ))}
              </DAppIconListWrapper>
            </div>
            <div>
              <DAppIconListTitle>
                <SecondTitle>Swap</SecondTitle>
              </DAppIconListTitle>
              <SwapContainer>
                <TransferWrapper>
                  <Select
                      style={{ color: '#000000', fontSize: '20px', fontWeight: 'bold' }}
                      native
                      value={fromCurrencyType}
                      onChange={this.handleChangeFrom}
                      input={<BootstrapInput />}
                  >
                    <option value='CLV'>CLV</option>
                    <option value='DOT'>DOT</option>
                    <option value='ETH'>ETH</option>
                    <option value='USDT'>USDT</option>
                  </Select>
                  <TransferImg src={TransferIcon}></TransferImg>
                  <Select
                      style={{ color: '#000000', fontSize: '20px', fontWeight: 'bold' }}
                      native
                      value={toCurrencyType}
                      onChange={this.handleChangeTo}
                      input={<BootstrapInput />}
                  >
                    <option value='CLV'>CLV</option>
                    <option value='DOT'>DOT</option>
                    <option value='ETH'>ETH</option>
                    <option value='USDT'>USDT</option>
                  </Select>
                  <TransactionButton onClick={() => { this.goDappsDetail('https://swap.clover.finance/#/'); }}>
                    <Text fontSize='13px'>transaction</Text>
                  </TransactionButton>
                </TransferWrapper>
                <Text fontSize='12px' marginTop='10px'><span style={{ marginRight: '10px' }}>PRICE</span>1DAL=2.3452CLV</Text>
              </SwapContainer>
            </div>

            <GrayBackground>
              <NavBar>
                {tabList.map((res: any) => (
                    res.id === currentIndex ? <NavLinkActive>{res.tabName}</NavLinkActive> : (
                        <NavLink onClick={() => { this.tabChoiced(res.id); }}>{res.tabName}</NavLink>
                    ))
                )}
              </NavBar>
              <DappListWrapper>
                <SwipeableViews style={{height: '100px'}}>
                  {tabList.map((res: any, index: number) => (
                      <div style={Object.assign({}, styles.slide, styles.slide1)}>
                        {dapplist.map((dapp: any) => (
                            <DappContainer onClick={() => { this.goDappsDetail(dapp.url); }}>
                              <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                              <DappContentWrapper>
                                <Column>
                                  <DappName>{dapp.dappName}</DappName>
                                  <DappDescription>{dapp.describe}</DappDescription>
                                </Column>
                              </DappContentWrapper>
                            </DappContainer>
                        ))}
                      </div>
                  ))}
                </SwipeableViews>
                {dapplist.map((dapp: any) => (
                  <DappContainer onClick={() => { this.goDappsDetail(dapp.url); }}>
                    <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                    <DappContentWrapper>
                      <Column>
                        <DappName>{dapp.dappName}</DappName>
                        <DappDescription>{dapp.describe}</DappDescription>
                      </Column>
                    </DappContentWrapper>
                  </DappContainer>
                ))}
              </DappListWrapper>
            </GrayBackground>
          </BodyWrapper>
        </AppBody>
    );
  }

}
