import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, {RowStart, RowCenter} from "../../components/Row";
import BackIcon from '../../assets/images/back_icon.svg'
import {Text} from "rebass";
import ImgDappExchange from "../../assets/images/polkadot_exchange.svg";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import SwipeableViews from "react-swipeable-views";

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(Row)`
  padding: 0 20px;
  margin-top: 50px;
  height: 44px;
  background-color: #ffffff;
`;

const MyDApps = styled(Text)`
  font-size: 20px;
  font-weight: bold;
  color: #333333;
  text-align: center;
  width: 100%;
`;

const GrayBackground = styled.div`
  width: 100%;
  overflow-x: hidden;
  background-color: #F7F7F7;
  height: calc(100vh - 92px);
`;

const NavBar = styled(RowStart)`
  margin-top: 15px;
  padding: 0 20px;
  align-items: flex-start;
`;

const NavLink = styled(Text)`
  font-size: 16px;
  font-weight: 500;
  color: #41485D;
  text-decoration: none;
  margin-right: 30px!important;
`;

const NavLinkActive = styled(NavLink)`
  color: #FB822A;
  padding-bottom: 3px;
  border-bottom: 2px solid #FB822A;
`;

const DappListWrapper = styled.div`
  background-color: #ffffff;
  margin: 10px 20px;
  border-radius: 10px;
  max-height: 88%;
  overflow-x: hidden;
  overflow-y: auto;
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  margin-top: 10px;
  margin-bottom: 10px;
  padding-bottom: 15px;
`;

const DappContentWrapper = styled(Column)`
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #666666;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 85%;
`;


export default class MoreDAppsComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            tabList: [
                {tabName:"Recent",id:1},
                {tabName:"Favorites",id:2},
            ],
            currentIndex: 0,
        }
    }
    goBack = () => {
        this.props.history.goBack();
    }

    goDappsDetail = (url: any) => {
        this.props.history.push({pathname: '/dappsdetail', state: {url: url}});
    }

    handleChange = (event: any, value: any) => {
        this.setState({
            currentIndex: value,
        });
    };

    handleChangeIndex = (index: any) => {
        this.setState({
            currentIndex: index,
        });
    };

    render() {
        const { tabList, currentIndex } = this.state;
        const dapplist = [
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            }
        ];
        const slideStyles = {
            tabs: {
                padding: '0 20px',
                // background: '#ffffff',
            },
            slide: {
                padding: 15,
                minHeight: 'fit-content',
                color: '#fff',
            }
        };
        return (
            <AppBody>
                <BodyWrapper gap={'0px'}>
                    <HeadContainer>
                        <img onClick={this.goBack} src={BackIcon} alt="" width='9px' height='16px' />
                        <MyDApps>My DApps</MyDApps>
                    </HeadContainer>

                    <GrayBackground>
                        <Tabs value={currentIndex} onChange={this.handleChange} style={slideStyles.tabs}
                              TabIndicatorProps={{
                                  style: {
                                      height: '4px',
                                      width: '17px',
                                      backgroundColor: 'rgba(246, 136, 59, 1)',
                                      borderRadius: '2px',
                                      marginLeft: '27px'
                                  }
                              }}
                        >
                            {tabList.map((res: any) => (
                                <Tab label={res.tabName}/>
                            ))}
                        </Tabs>
                        <DappListWrapper>
                            <SwipeableViews index={currentIndex} onChangeIndex={this.handleChangeIndex}>
                                {tabList.map(() => (
                                    <div style={slideStyles.slide}>
                                        {dapplist.map((dapp: any) => (
                                            <DappContainer onClick={() => { this.goDappsDetail(dapp.url); }}>
                                                <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                                                <DappContentWrapper>
                                                    <Column>
                                                        <DappName>{dapp.dappName}</DappName>
                                                        <DappDescription>{dapp.describe}</DappDescription>
                                                    </Column>
                                                </DappContentWrapper>
                                            </DappContainer>
                                        ))}
                                    </div>
                                ))}
                            </SwipeableViews>
                        </DappListWrapper>
                    </GrayBackground>

                    {/*<GrayBackground>*/}
                    {/*    <NavBar>*/}
                    {/*        {tabList.map((res: any, index: number) => (*/}
                    {/*            res.id === currentIndex ? <NavLinkActive>{res.tabName}</NavLinkActive> : (*/}
                    {/*                <NavLink onClick={() => { this.tabChoiced(res.id); }}>{res.tabName}</NavLink>*/}
                    {/*            ))*/}
                    {/*        )}*/}
                    {/*    </NavBar>*/}

                    {/*    <DappListWrapper>*/}
                    {/*        {dapplist.map((dapp: any) => (*/}
                    {/*            <DappContainer onClick={() => { this.goDappsDetail(dapp.url); }}>*/}
                    {/*                <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />*/}
                    {/*                <DappContentWrapper>*/}
                    {/*                    <Column>*/}
                    {/*                        <DappName>{dapp.dappName}</DappName>*/}
                    {/*                        <DappDescription>{dapp.describe}</DappDescription>*/}
                    {/*                    </Column>*/}
                    {/*                </DappContentWrapper>*/}
                    {/*            </DappContainer>*/}
                    {/*        ))}*/}
                    {/*    </DappListWrapper>*/}
                    {/*</GrayBackground>*/}
                </BodyWrapper>
            </AppBody>
        );
    }
}
