import React, { Component } from 'react';
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, { RowBetween, RowCenter } from '../../components/Row'
import Application from '../../assets/images/application.svg'
import SwapIcon from '../../assets/images/swap_icon.svg'
import LendingIcon from '../../assets/images/lending_icon.svg'
import AggregationIcon from '../../assets/images/aggregation_icon.svg'
import AuctionIcon from '../../assets/images/auction_icon.svg'
import InsuranceIcon from '../../assets/images/insurance_icon.svg'
import TransferIcon from '../../assets/images/transfer_icon.svg'
import Polkswap from '../../assets/images/polkaswap.png'
import MantarDao from '../../assets/images/mantarDao.png'
import ReefFinance from '../../assets/images/reefFinance.png'
import Ren from '../../assets/images/ren.png'
import Xaya from '../../assets/images/xaya.png'
import Polkastarter from '../../assets/images/polkastarter.png'
import ChadaVC from '../../assets/images/chadaVC.png'
import SelectDown from '../../assets/images/select_down.svg'
import SwipeableViews from 'react-swipeable-views';
import Select from '@material-ui/core/Select';
import withStyles from '@material-ui/core/styles/withStyles';
import InputBase from '@material-ui/core/InputBase';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import './style.css';

const BodyWrapper = styled.div`
  width: 100%;
  background: linear-gradient(180deg, #FFFFFF 0%, #FFFFFF 46%, #F5F5F5 100%);
`;

const BodyContent = styled(AutoColumn)`
  width: 1024px;
  margin: 0 auto;
`;

const HeadContainer = styled(RowBetween)`
  height: 100px;
`;

const DAppIconListTitle = styled(RowBetween)`
  margin-bottom: 20px;
`;

const DAppIconListWrapper = styled(RowBetween)`
  margin-bottom: 40px;
`;

const DappIconWrapper = styled.div`
  text-align: center;
  flex-grow: 1;
  cursor: pointer;
`;

const DappIconName = styled(Text)`
  font-size: 14px;
  color: #666666;
  padding-top: 15px;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const SwapContainer = styled.div`
  margin-top: 15px;
  padding: 0 45px;
  background: rgba(246, 136, 59, 0.08);
  border-radius: 20px;
  border: 2px solid #FF7D30;
  height: 100px;
  display: flex;
  align-items: center;
`;

const TransferWrapper = styled(Row)`
  position: relative;
  width: 100%;
`;

const TransferSelect = styled.div`
  width: 50%;
  border-right: 1px solid rgba(255, 125, 48, 0.24);
`;

const TransferImg = styled.img`
  width: 60px;
  height: 60px;
  margin: 0 40px;
  vertical-align: middle;
`

const SecondTitle = styled(Text)`
  font-size: 22px;
  color: #333333;
  font-weight: 600;
`;

const MoreButton = styled(Text)`
  cursor: pointer;
  font-size: 16px;
  color: #333333;
  :hover {
    color: #F96710;
  }
`;

const GrayBackground = styled.div`
  width: 100%;
  overflow-x: hidden;
  height: fit-content;
`;

const SwapButton = styled(RowCenter)`
  padding: 0 15px;
  height: 38px;
  width: 88px;
  background: #FF7D30;
  border-radius: 35px;
  color: #ffffff;
  position: absolute;
  right: 0;
  cursor: pointer;
  :hover {
    background-color: #F96710;
  }
`;

const DappContainer = styled.div`
  cursor: pointer;
  display: inline-block;
  width: 499px;
  height: 119px;
  background: #ffffff;
  border-radius: 20px;
  padding: 20px 30px;
  margin-bottom: 20px;
  border: 1px solid #ffffff;
  :nth-child(odd) {
    margin-right: 20px;
  }
  :hover {
    border-color: #333;
  }
`;

const DappContentWrapper = styled(Column)`
  display: inline-block;
  vertical-align: middle;
  max-width: 75%;
  margin-left: 20px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 18px;
  padding-bottom: 10px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 14px;
  font-weight: normal;
  color: #666666;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const SearchContainer = styled.div`
  display: grid;
  grid-template-columns: auto 75px;
  width: 400px;
  height: 44px;
  background: #FFFFFF;
  border-radius: 67px;
  border: 2px solid #FF7D30;
`;

const SearchInput = styled.input`
  text-indent: 20px;
  outline: none;
  border: none;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 16px;
  color: #333333;
  background: transparent;

  ::placeholder {
    color: rgba(65, 72, 93, 0.5);
  }
`;

const SearchButton = styled.span`
  display: inline-block;
  width: 78px;
  height: 40px;
  line-height: 40px;
  color: #ffffff;
  font-size: 16px;
  text-align: center;
  background: #FF7D30;
  border-bottom-right-radius: 59px;
  border-top-right-radius: 59px;
  cursor: pointer;

  :hover {
    background-color: #F96710;
  }
`;


export default class DappMWeb extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        const dappLists = [
            [
                {
                    dappName: 'Polkaswap',
                    describe: 'Polkaswap is a DEX project for the Polkadot ecosystem. It provides a standard bridging interface and will give the easy possibility to list and exchange tokens for all substrate parachains and other blockchains like Ethereum and Bitcoin.',
                    url: 'https://polkaswap.io/',
                    icon: Polkswap
                },
                {
                    dappName: 'MANTRA DAO',
                    describe: 'MANTRA DAO is a community governed DeFi platform focusing on staking, lending, and governance.',
                    url: 'https://mantradao.com/',
                    icon: MantarDao
                },
                {
                    dappName: 'Reef Finance',
                    describe: 'Reef the crosschain DeFi operating system',
                    url: 'https://reef.finance/',
                    icon: ReefFinance
                },
            ],
            [
                {
                    dappName: 'Ren',
                    describe: 'Ren is an open protocol that enables the permissionless and private transfer of value between any blockchain.',
                    url: 'https://renproject.io/',
                    icon: Ren
                }
            ],
            [
                {
                    dappName: 'Xaya',
                    describe: 'Decentralised Complex Gaming',
                    url: 'https://xaya.io/',
                    icon: Xaya
                }
            ],
            [
                {
                    dappName: 'Polkaswap',
                    describe: 'Polkaswap is a DEX project for the Polkadot ecosystem. It provides a standard bridging interface and will give the easy possibility to list and exchange tokens for all substrate parachains and other blockchains like Ethereum and Bitcoin.',
                    url: 'https://polkaswap.io/',
                    icon: Polkswap
                },
                {
                    dappName: 'Polkastarter',
                    describe: 'Polkastarter is a permissionless DEX built for cross-chain token pools and auctions, enabling projects to raise capital on a decentralized and interoperable environment based on Polkadot.',
                    url: 'https://www.polkastarter.com/',
                    icon: Polkastarter
                }
            ],
            [
                {
                    dappName: 'Chads.VC',
                    describe: 'CHAD is the first deflationary MEME token built on Polkadot',
                    url: 'https://www.chads.vc/',
                    icon: ChadaVC
                }
            ],
        ]
        this.state = {
            tabList: [
                {tabName: "DeFi", id: 1},
                {tabName: "Bridge", id: 2},
                {tabName: "Grant", id: 3},
                {tabName: "Exchange", id: 4},
                {tabName: "NFT", id: 5}
            ],
            fromCurrencyType: 'CLV',
            toCurrencyType: 'ETH',
            currentIndex: 0,
            allDappList: dappLists,
            dapplist: dappLists[0],
            searchStr: ''
        }
    }

    searchDapps = () => {
        // console.log(this.state.searchStr);
    }

    changeSearchStr = (event: any) => {
        this.setState({
            searchStr: event.target.value
        })
    }

    moreDapps = (title: string) => {
        this.props.history.push({pathname: '/moredapps', state: {title: title}});
    }

    goDappsDetail = (url: any, dappName: string) => {
        this.props.history.push({pathname: '/dappsdetail', state: {url: url, dappName: dappName}});
    }

    handleChangeFrom = (event: any) => {
        this.setState({
            fromCurrencyType: event.target.value
        });
    };

    handleChangeTo = (event: any) => {
        this.setState({
            toCurrencyType: event.target.value
        });
    };

    handleChange = (event: any, value: any) => {
        const { allDappList } = this.state;
        this.setState({
            currentIndex: value,
            dapplist: allDappList[value]
        });
    };

    handleChangeIndex = (index: any) => {
        const { allDappList } = this.state;
        this.setState({
            currentIndex: index,
            dapplist: allDappList[index]
        });
    };

    render() {
        const { fromCurrencyType, toCurrencyType, tabList, currentIndex, dapplist, searchStr } = this.state;

        const recommendDapps = [
            {
                icon: SwapIcon,
                appName: 'Swap'
            },
            {
                icon: LendingIcon,
                appName: 'Lending'
            },
            {
                icon: AggregationIcon,
                appName: 'Aggregation'
            },
            {
                icon: AuctionIcon,
                appName: 'Auction'
            },
            {
                icon: InsuranceIcon,
                appName: 'Insurance'
            },
            {
                icon: SwapIcon,
                appName: 'Swap'
            },
            {
                icon: LendingIcon,
                appName: 'Lending'
            },
            {
                icon: AggregationIcon,
                appName: 'Aggregation'
            },
            {
                icon: InsuranceIcon,
                appName: 'Insurance'
            },
        ];

        const slideStyles = {
            tabs: {
                height: '65px',
                margin: '40px 0 20px 0',
            },
            slide: {
                padding: '10px 0',
                minHeight: 'fit-content',
                color: '#fff',
                overflow: 'hidden'
            }
        };

        const BootstrapInput = withStyles((theme) => ({
            input: {
                position: 'relative',
                border: 'none',
                fontSize: 24,
                fontWeight: 'bold',
                color: '#000000',
                width: 'fit-content',
                '&:focus': {
                    backgroundColor: 'rgba(246, 136, 59, 0.08)'
                },
            },
        }))(InputBase);

        const AntTab = withStyles((theme) => ({
            root: {
                textTransform: 'none',
                fontSize: '18px',
                '&$selected': {
                    color: '#FF7D30',
                    fontWeight: theme.typography.fontWeightMedium,
                }
            },
            selected: {
            },
        }))(Tab);
        const StyledTabs = withStyles({
            root: {
                height: '55px',
                margin: '40px 0 20px'
            },
            indicator: {
                display: 'flex',
                justifyContent: 'center',
                backgroundColor: 'transparent',
                '& > span': {
                    height: '4px',
                    width: '34px',
                    marginTop: '-4px',
                    backgroundColor: 'rgba(246, 136, 59, 1)',
                    borderRadius: '2px',
                },
            },
        })((props) => <Tabs {...props} value={currentIndex} onChange={this.handleChange} TabIndicatorProps={{ children: <span /> }} />);
        return (
            <AppBody>
                <BodyWrapper>
                    <BodyContent gap={'md'}>
                        <HeadContainer>
                            <img width="168" height="20" src={Application} alt=""/>
                            <SearchContainer>
                                <SearchInput placeholder='Search for Dapps' onChange={this.changeSearchStr} />
                                <SearchButton onClick={this.searchDapps}>GO</SearchButton>
                            </SearchContainer>
                        </HeadContainer>
                        <div>
                            <DAppIconListTitle>
                                <SecondTitle>My DApps</SecondTitle>
                                <MoreButton onClick={() => { this.moreDapps('My Dapps'); }}>more</MoreButton>
                            </DAppIconListTitle>
                            <DAppIconListWrapper>
                                {recommendDapps.map((dapp: any, index: number) => (
                                    <DappIconWrapper key={`MyDApps${index}`} onClick={() => { this.goDappsDetail('https://swap.clover.finance/#/', dapp.appName); }}>
                                        <img width='75' height='75' src={dapp.icon} alt="" />
                                        <DappIconName>{dapp.appName}</DappIconName>
                                    </DappIconWrapper>
                                ))}
                            </DAppIconListWrapper>
                        </div>
                        <div>
                            <DAppIconListTitle>
                                <SecondTitle>Recommend DApps</SecondTitle>
                                <MoreButton onClick={() => { this.moreDapps('My Dapps'); }}>more</MoreButton>
                            </DAppIconListTitle>
                            <DAppIconListWrapper>
                                {recommendDapps.map((dapp: any, index: number) => (
                                    <DappIconWrapper key={`RecommendDApps${index}`} onClick={() => { this.goDappsDetail('https://swap.clover.finance/#/', dapp.appName); }}>
                                        <img width='75' height='75' src={dapp.icon} alt="" />
                                        <DappIconName>{dapp.appName}</DappIconName>
                                    </DappIconWrapper>
                                ))}
                            </DAppIconListWrapper>
                        </div>
                        <div>
                            <DAppIconListTitle>
                                <SecondTitle>Swap</SecondTitle>
                            </DAppIconListTitle>
                            <SwapContainer>
                                <TransferWrapper>
                                    <TransferSelect>
                                        <Select
                                            style={{ verticalAlign: 'middle' }}
                                            native
                                            value={fromCurrencyType}
                                            onChange={this.handleChangeFrom}
                                            input={<BootstrapInput />}
                                            IconComponent={() => (<img src={SelectDown} width='17px' height='10px' style={{margin: '0 5px 0 -15px'}} />)}
                                        >
                                            <option value='CLV'>CLV</option>
                                            <option value='DOT'>DOT</option>
                                            <option value='ETH'>ETH</option>
                                            <option value='USDT'>USDT</option>
                                        </Select>
                                        <TransferImg src={TransferIcon}></TransferImg>
                                        <Select
                                            style={{ verticalAlign: 'middle' }}
                                            native
                                            value={toCurrencyType}
                                            onChange={this.handleChangeTo}
                                            input={<BootstrapInput />}
                                            IconComponent={() => (<img src={SelectDown} width='18px' height='11px' style={{margin: '0 5px 0 -15px'}} />)}
                                        >
                                            <option value='CLV'>CLV</option>
                                            <option value='DOT'>DOT</option>
                                            <option value='ETH'>ETH</option>
                                            <option value='USDT'>USDT</option>
                                        </Select>
                                    </TransferSelect>
                                    <Text fontSize='16px' marginLeft='50px'><span style={{ marginRight: '15px' }}>PRICE</span>1DAL=2.3452CLV</Text>
                                    <SwapButton onClick={() => { this.goDappsDetail('https://swap.clover.finance/#/', 'Swap'); }}>
                                        <Text fontSize='16px'>Swap</Text>
                                    </SwapButton>
                                </TransferWrapper>
                            </SwapContainer>
                        </div>
                        <GrayBackground>
                            <StyledTabs>
                                {tabList.map((res: any) => (
                                    <AntTab key={res.tabName} label={res.tabName} />
                                ))}
                            </StyledTabs>
                            <SwipeableViews index={currentIndex} onChangeIndex={this.handleChangeIndex} style={{ height: '100%' }} enableMouseEvents>
                                {tabList.map((tab: any, index: number) => (
                                    <div key={`slide_${index}`} style={slideStyles.slide}>
                                        {dapplist.map((dapp: any) => (
                                            <DappContainer key={`slide_${dapp.dappName}`} onClick={() => { this.goDappsDetail(dapp.url, dapp.dappName); }}>
                                                <img height="76" width="76" src={dapp.icon} alt='Polkadot Exchange' />
                                                <DappContentWrapper>
                                                    <Column>
                                                        <DappName>{dapp.dappName}</DappName>
                                                        <DappDescription>{dapp.describe}</DappDescription>
                                                    </Column>
                                                </DappContentWrapper>
                                            </DappContainer>
                                        ))}
                                    </div>
                                ))}
                            </SwipeableViews>
                        </GrayBackground>
                    </BodyContent>
                </BodyWrapper>
            </AppBody>
        );
    }
}
