import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, {RowStart, RowCenter} from "../../components/Row";
import BackIcon from '../../assets/images/back_icon.svg'
import {Text} from "rebass";

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(Row)`
  padding: 0 20px;
  margin-top: 50px;
  background-color: #ffffff;
`;

const MyDApps = styled(Text)`
  font-size: 20px;
  font-weight: bold;
  color: #333333;
  text-align: center;
  width: 100%;
`;

const IframeContainer = styled.div`
  width: 100%;
  height: calc(100vh - 92px);
`;

export default class DAppsDetailComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
    }

    goBack = () => {
        this.props.history.goBack();
    }

    render() {
        const iframeUrl = this.props.location.state ? this.props.location.state.url : null;
        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <img onClick={this.goBack} src={BackIcon} alt="" width='9px' height='16px' />
                        <MyDApps>DApps Detail</MyDApps>
                    </HeadContainer>
                    <IframeContainer>
                        {iframeUrl ? <iframe
                            src={iframeUrl}
                            width="100%"
                            height="100%"
                            frameBorder='none'
                        ></iframe> : null}
                    </IframeContainer>
                </BodyWrapper>
            </AppBody>
        );
    }
}
