import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, {RowBetween, RowCenter} from "../../components/Row";
import SelectDown from '../assets/images/select_down.svg'
import BackIcon from '../../assets/images/back_icon.svg'
import {Text} from "rebass";
import ImgDappExchange from "../../assets/images/polkadot_exchange.svg";
import ImgDappData from "../../assets/images/polkadot_data.svg";

const BodyWrapper = styled(AutoColumn)`
  padding-left: 20px;
  padding-right: 20px;
`;

const HeadContainer = styled(Row)`
  margin-top: 50px;
  background-color: #ffffff;
`;

const MyDApps = styled(Text)`
  font-size: 20px;
  font-weight: bold;
  color: #333333;
  text-align: center;
  width: 100%;
`;

const GrayBackground = styled.div`
  background-color: #F7F7F7;
`;

const NavBar = styled(RowBetween)`
  margin-top: 15px;
  padding: 0 20px;
  align-items: flex-start;
`;

const NavLink = styled(Text)`
  font-size: 16px;
  font-weight: 500;
  color: #41485D;
  text-decoration: none;
`;

const NavLinkActive = styled(NavLink)`
  color: #FB822A;
  padding-bottom: 3px;
  border-bottom: 2px solid #FB822A;
`;

const DappListWrapper = styled.div`
  background-color: #ffffff;
  margin: 0 20px;
  border-radius: 10px;
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  margin-top: 10px;
  margin-bottom: 10px;
  padding: 15px;
`;

const DappContentWrapper = styled(Column)`
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #666666;
`;


export default class MoreDAppsComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            tabList: [
                {tabName:"Recent",id:1},
                {tabName:"Favorites",id:2},
            ],
            currentIndex: 1,
        }
    }
    goBack = () => {
        this.props.history.goBack();
    }

    tabChoiced = (id: any) => {
        this.setState({
            currentIndex:id
        });
    }

    render() {
        const { tabList, currentIndex } = this.state;
        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <img onClick={this.goBack} src={BackIcon} alt="" width='9px' height='16px' />
                        <MyDApps>My DApps</MyDApps>
                    </HeadContainer>
                    <GrayBackground>
                        <NavBar>
                            {tabList.map((res: any, index: number) => (
                                res.id === currentIndex ? <NavLinkActive>{res.tabName}</NavLinkActive> : (
                                    <NavLink onClick={() => { this.tabChoiced(res.id); }}>{res.tabName}</NavLink>
                                ))
                            )}
                        </NavBar>

                        <DappListWrapper>
                            <DappContainer>
                                <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                                <DappContentWrapper>
                                    <Column>
                                        <DappName>CLV Mining</DappName>
                                        <DappDescription>Clover Mining Platform</DappDescription>
                                    </Column>
                                </DappContentWrapper>
                            </DappContainer>

                            <DappContainer>
                                <img height="50" width="50" src={ImgDappData} alt='Polkadot Data' />
                                <DappContentWrapper>
                                    <Column>
                                        <DappName>CLV Mining</DappName>
                                        <DappDescription>Clover Mining Platform</DappDescription>
                                    </Column>
                                </DappContentWrapper>
                            </DappContainer>

                            <DappContainer>
                                <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                                <DappContentWrapper>
                                    <Column>
                                        <DappName>CLV Mining</DappName>
                                        <DappDescription>Clover Mining Platform</DappDescription>
                                    </Column>
                                </DappContentWrapper>
                            </DappContainer>
                        </DappListWrapper>
                    </GrayBackground>
                </BodyWrapper>
            </AppBody>
        );
    }
}
