import React, { Component } from 'react';
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, { RowBetween, RowCenter } from '../../components/Row'
import Application from '../../assets/images/application.svg'
import SearchIcon from '../../assets/images/search_icon.svg'
import SwapIcon from '../../assets/images/swap_icon.svg'
import LendingIcon from '../../assets/images/lending_icon.svg'
import AggregationIcon from '../../assets/images/aggregation_icon.svg'
import AuctionIcon from '../../assets/images/auction_icon.svg'
import InsuranceIcon from '../../assets/images/insurance_icon.svg'
import TransferIcon from '../../assets/images/transfer_icon.svg'
import Polkswap from '../../assets/images/polkaswap.png'
import MantarDao from '../../assets/images/mantarDao.png'
import ReefFinance from '../../assets/images/reefFinance.png'
import Ren from '../../assets/images/ren.png'
import Xaya from '../../assets/images/xaya.png'
import Polkastarter from '../../assets/images/polkastarter.png'
import ChadaVC from '../../assets/images/chadaVC.png'
import SelectDown from '../../assets/images/select_down.svg'
import SwipeableViews from 'react-swipeable-views';
import Select from '@material-ui/core/Select';
import withStyles from '@material-ui/core/styles/withStyles';
import InputBase from '@material-ui/core/InputBase';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import './style.css';

const BodyWrapper = styled(AutoColumn)`
  //max-width: 1280px;
  //min-width: 1024px;
  width: 1280px;
  margin: 0 auto;
`;

const HeadContainer = styled(RowBetween)`
  //margin: 20px 0;
  padding: 0 20px;
  height: 120px;
`;

const DAppIconListTitle = styled(RowBetween)`
  padding: 0 20px;
  height: 94px;
  margin-top: 40px;
`;

const DAppIconListWrapper = styled(RowBetween)`
  margin-top: 15px;
  padding: 0 20px;
`;

const DappIconWrapper = styled.div`
  text-align: center;
  flex-grow: 1;
  width: 50px;
`;

const DappIconName = styled(Text)`
  font-size: 20px;
  color: #666666;
  padding-top: 14px;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const SwapContainer = styled.div`
  margin-top: 15px;
  padding: 0 70px;
  background: rgba(246, 136, 59, 0.08);
  border-radius: 30px;
  border: 2px solid #FF7D30;
  height: 150px;
  display: flex;
  align-items: center;
`;

const TransferWrapper = styled(Row)`
  position: relative;
  width: 100%;
`;

const TransferSelect = styled.div`
  width: 50%;
  border-right: 1px solid rgba(255, 125, 48, 0.24);
`;

const TransferImg = styled.img`
  width: 76px;
  height: 76px;
  margin: 0 50px;
  vertical-align: middle;
`

const SecondTitle = styled(Text)`
  font-size: 30px;
  color: #333333;
  font-weight: 600;
`;

const GrayBackground = styled.div`
  width: 100%;
  overflow-x: hidden;
  height: fit-content;
  //background: linear-gradient(180deg, #FFFFFF 0%, #F3f3f3 100%);
`;

const SwapButton = styled(RowCenter)`
  padding: 0 15px;
  height: 70px;
  width: 150px;
  background: #FF7D30;
  border-radius: 35px;
  color: #ffffff;
  position: absolute;
  right: 0;
`;

const Search = styled.img`
  margin-left: 20px;
  width: 54px;
  height: 54px;
`;

const DappListWrapper = styled(RowBetween)`
  //background-color: #ffffff;
  //margin: 0 20px;
  //border-radius: 10px;
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  display: inline-block;
  padding: 15px;
  width: 620px;
  height: 156px;
  background: #ffffff;
  border-radius: 30px;
  padding: 30px;
`;

const DappContentWrapper = styled(Column)`
  max-width: 80%;
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #666666;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

export default class DappMWeb extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        const dappLists = [
            [
                {
                    dappName: 'Polkaswap',
                    describe: 'Polkaswap is a DEX project for the Polkadot ecosystem. It provides a standard bridging interface and will give the easy possibility to list and exchange tokens for all substrate parachains and other blockchains like Ethereum and Bitcoin.',
                    url: 'https://polkaswap.io/',
                    icon: Polkswap
                },
                {
                    dappName: 'MANTRA DAO',
                    describe: 'MANTRA DAO is a community governed DeFi platform focusing on staking, lending, and governance.',
                    url: 'https://mantradao.com/',
                    icon: MantarDao
                },
                {
                    dappName: 'Reef Finance',
                    describe: 'Reef the crosschain DeFi operating system',
                    url: 'https://reef.finance/',
                    icon: ReefFinance
                },
            ],
            [
                {
                    dappName: 'Ren',
                    describe: 'Ren is an open protocol that enables the permissionless and private transfer of value between any blockchain.',
                    url: 'https://renproject.io/',
                    icon: Ren
                }
            ],
            [
                {
                    dappName: 'Xaya',
                    describe: 'Decentralised Complex Gaming',
                    url: 'https://xaya.io/',
                    icon: Xaya
                }
            ],
            [
                {
                    dappName: 'Polkaswap',
                    describe: 'Polkaswap is a DEX project for the Polkadot ecosystem. It provides a standard bridging interface and will give the easy possibility to list and exchange tokens for all substrate parachains and other blockchains like Ethereum and Bitcoin.',
                    url: 'https://polkaswap.io/',
                    icon: Polkswap
                },
                {
                    dappName: 'Polkastarter',
                    describe: 'Polkastarter is a permissionless DEX built for cross-chain token pools and auctions, enabling projects to raise capital on a decentralized and interoperable environment based on Polkadot.',
                    url: 'https://www.polkastarter.com/',
                    icon: Polkastarter
                }
            ],
            [
                {
                    dappName: 'Chads.VC',
                    describe: 'CHAD is the first deflationary MEME token built on Polkadot',
                    url: 'https://www.chads.vc/',
                    icon: ChadaVC
                }
            ],
        ]
        this.state = {
            tabList: [
                {tabName: "DeFi", id: 1},
                {tabName: "Bridge", id: 2},
                {tabName: "Grant", id: 3},
                {tabName: "Exchange", id: 4},
                {tabName: "NFT", id: 5}
            ],
            fromCurrencyType: 'CLV',
            toCurrencyType: 'ETH',
            currentIndex: 0,
            allDappList: dappLists,
            dapplist: dappLists[0],
        }
    }

    searchDapps = () => {
        this.props.history.push('/search');
    }

    moreDapps = () => {
        this.props.history.push('/moredapps');
    }

    goDappsDetail = (url: any, dappName: string) => {
        this.props.history.push({pathname: '/dappsdetail', state: {url: url, dappName: dappName}});
    }

    handleChangeFrom = (event: any) => {
        this.setState({
            fromCurrencyType: event.target.value
        });
    };

    handleChangeTo = (event: any) => {
        this.setState({
            toCurrencyType: event.target.value
        });
    };

    handleChange = (event: any, value: any) => {
        const { allDappList } = this.state;
        this.setState({
            currentIndex: value,
            dapplist: allDappList[value]
        });
    };

    handleChangeIndex = (index: any) => {
        const { allDappList } = this.state;
        this.setState({
            currentIndex: index,
            dapplist: allDappList[index]
        });
    };

    render() {
        const { fromCurrencyType, toCurrencyType, tabList, currentIndex, dapplist } = this.state;

        const recommendDapps = [
            {
                icon: SwapIcon,
                appName: 'Swap'
            },
            {
                icon: LendingIcon,
                appName: 'Lending'
            },
            {
                icon: AggregationIcon,
                appName: 'Aggregation'
            },
            {
                icon: AuctionIcon,
                appName: 'Auction'
            },
            {
                icon: InsuranceIcon,
                appName: 'Insurance'
            },
            {
                icon: SwapIcon,
                appName: 'Swap'
            },
            {
                icon: LendingIcon,
                appName: 'Lending'
            },
            {
                icon: AggregationIcon,
                appName: 'Aggregation'
            },
        ];

        const slideStyles = {
            tabs: {
                margin: '40px 0 20px 0',
            },
            slide: {
                padding: '10px 0',
                minHeight: 'fit-content',
                color: '#fff',
                overflow: 'hidden'
            }
        };

        const BootstrapInput = withStyles((theme) => ({
            input: {
                position: 'relative',
                border: 'none',
                fontSize: 30,
                width: 'fit-content',
                '&:focus': {
                    backgroundColor: 'rgba(246, 136, 59, 0.08)'
                },
            },
        }))(InputBase);

        const AntTab = withStyles((theme) => ({
            root: {
                textTransform: 'none',
                fontSize: '26px',
                '&$selected': {
                    color: '#FF7D30',
                    fontWeight: theme.typography.fontWeightMedium,
                },
            },
            selected: {},
        }))(Tab);

        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <div className={this.state.isShow}>
                        <HeadContainer>
                            <img width="245" height="30" src={Application} alt=""/>
                            <Search src={SearchIcon} alt='search' onClick={this.searchDapps} />
                        </HeadContainer>
                        <div>
                            <DAppIconListTitle>
                                <SecondTitle>My DApps</SecondTitle>
                                <Text color='#333333' fontSize='20px' onClick={this.moreDapps}>more</Text>
                            </DAppIconListTitle>
                            <DAppIconListWrapper>
                                {recommendDapps.map((dapp) => (
                                    <DappIconWrapper onClick={() => { this.goDappsDetail('https://swap.clover.finance/#/', dapp.appName); }}>
                                        <img width='100' height='100' src={dapp.icon} alt="" />
                                        <DappIconName>{dapp.appName}</DappIconName>
                                    </DappIconWrapper>
                                ))}
                            </DAppIconListWrapper>
                        </div>
                        <div>
                            <DAppIconListTitle>
                                <SecondTitle>Recommend DApps</SecondTitle>
                                <Text color='#333333' fontSize='20px' onClick={this.moreDapps}>more</Text>
                            </DAppIconListTitle>
                            <DAppIconListWrapper>
                                {recommendDapps.map((dapp) => (
                                    <DappIconWrapper onClick={() => { this.goDappsDetail('https://swap.clover.finance/#/', dapp.appName); }}>
                                        <img width='100' height='100' src={dapp.icon} alt="" />
                                        <DappIconName>{dapp.appName}</DappIconName>
                                    </DappIconWrapper>
                                ))}
                            </DAppIconListWrapper>
                        </div>
                        <div>
                            <DAppIconListTitle>
                                <SecondTitle>Swap</SecondTitle>
                            </DAppIconListTitle>
                            <SwapContainer>
                                <TransferWrapper>
                                    <TransferSelect>
                                        <Select
                                            style={{ color: '#000000', fontSize: '30px', fontWeight: 'bold', verticalAlign: 'middle' }}
                                            native
                                            value={fromCurrencyType}
                                            onChange={this.handleChangeFrom}
                                            input={<BootstrapInput />}
                                            IconComponent={() => (<img src={SelectDown} width='18px' height='11px' />)}
                                        >
                                            <option value='CLV'>CLV</option>
                                            <option value='DOT'>DOT</option>
                                            <option value='ETH'>ETH</option>
                                            <option value='USDT'>USDT</option>
                                        </Select>
                                        <TransferImg src={TransferIcon}></TransferImg>
                                        <Select
                                            style={{ color: '#000000', fontSize: '30px', fontWeight: 'bold', verticalAlign: 'middle' }}
                                            native
                                            value={toCurrencyType}
                                            onChange={this.handleChangeTo}
                                            input={<BootstrapInput />}
                                            IconComponent={() => (<img src={SelectDown} width='18px' height='11px' />)}
                                        >
                                            <option value='CLV'>CLV</option>
                                            <option value='DOT'>DOT</option>
                                            <option value='ETH'>ETH</option>
                                            <option value='USDT'>USDT</option>
                                        </Select>
                                    </TransferSelect>
                                    <Text fontSize='20px' marginLeft='40px'><span style={{ marginRight: '20px' }}>PRICE</span>1DAL=2.3452CLV</Text>
                                    <SwapButton onClick={() => { this.goDappsDetail('https://swap.clover.finance/#/', 'Swap'); }}>
                                        <Text fontSize='24px'>Swap</Text>
                                    </SwapButton>
                                </TransferWrapper>
                            </SwapContainer>
                        </div>
                    </div>
                    <GrayBackground>
                        <Tabs className={this.state.stickyClass} value={currentIndex} onChange={this.handleChange} style={slideStyles.tabs} variant='scrollable'
                              TabIndicatorProps={{
                                  style: {
                                      height: '4px',
                                      width: '17px',
                                      backgroundColor: 'rgba(246, 136, 59, 1)',
                                      borderRadius: '2px',
                                      marginLeft: '5.5%'
                                  }
                              }}
                        >
                            {tabList.map((res: any) => (
                                <AntTab label={res.tabName}/>
                            ))}
                        </Tabs>
                        <DappListWrapper className={this.state.contentClass}>
                            <SwipeableViews index={currentIndex} onChangeIndex={this.handleChangeIndex} style={{ height: '100%' }}>
                                {tabList.map(() => (
                                    <div style={slideStyles.slide}>
                                        {dapplist.map((dapp: any) => (
                                            <DappContainer onClick={() => { this.goDappsDetail(dapp.url, dapp.dappName); }}>
                                                <img height="50" width="50" src={dapp.icon} alt='Polkadot Exchange' />
                                                <DappContentWrapper>
                                                    <Column>
                                                        <DappName>{dapp.dappName}</DappName>
                                                        <DappDescription>{dapp.describe}</DappDescription>
                                                    </Column>
                                                </DappContentWrapper>
                                            </DappContainer>
                                        ))}
                                    </div>
                                ))}
                            </SwipeableViews>
                        </DappListWrapper>
                    </GrayBackground>
                </BodyWrapper>
            </AppBody>
        );
    }
}
