import React, {Component} from 'react'
import styled from 'styled-components'
import AppBody, { AppContentWrapper } from '../AppBody'
import { AutoColumn, ColumnCenter } from '../../components/Column'
import {Text} from "rebass";
import Search from "../../assets/images/search.svg";
import HeaderBackground from "../../assets/images/header_background.svg";

const BodyWrapper = styled(AutoColumn)`
  width: 1240px;
  margin: 0 auto;
`;

const ContentWrapper = styled.div`
  height: 379px;
  width: 100%;
  background: linear-gradient(180deg, #1E455C 0%, #153040 100%);
  padding-top: 47px;
`

const ContentContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  padding-top: 30px;
  width: 1240px;
  height: 384px;
  margin: 0 auto;
  background: url('${HeaderBackground}');
`;
const SearchContainer = styled.div`
  display: grid;
  grid-template-columns: auto 50px;
  width: 754px;
  height: 52px;
  background: #FFFFFF;
  border-radius: 25px;
  border: 1px solid #D5DAE2;
  margin-top: 42px;
`;

const SearchInput = styled.input`
  text-indent: 20px;
  outline: none;
  border: none;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 16px;
  color: #333333;
  background: transparent;

  ::placeholder {
    color: rgba(65, 72, 93, 0.5);
  }
`;

const SearchButton = styled.span`
  display: inline-block;
  line-height: 46px;
  color: #ffffff;
  font-size: 16px;
  text-align: center;
  width: 46px;
  height: 46px;
  background: linear-gradient(187deg, #8BED7F 0%, #48C079 100%);
  border-radius: 59px;
  cursor: pointer;
  margin-top: 2px;
`;

const ContentTop = styled.div`
  width: 100%;
  height: 185px;
  background: #ffffff;
  box-shadow: 0px 2px 7px 0px #E9EDEA;
  border-radius: 8px;
  position: relative;
  top: -50px;
  z-index: 10;
`;


// export default function Assets(): React.ReactElement {
export default class Assets extends Component<any, any> {
  constructor(props: any, context: any) {
    super(props, context);
    this.state = {
      searchStr: ''
    }
  }
  searchDapps = () => {
    // console.log(this.state.searchStr);
  }

  changeSearchStr = (event: any) => {
    this.setState({
      searchStr: event.target.value
    })
  }
  render() {
    return (
        <AppBody>
          <ContentWrapper>
            <ContentContainer>
              <Text fontSize={44} fontWeight='bold' color='#ffffff' textAlign='center'>The Clover Blockchain Explorer</Text>
              <Text fontSize={23} fontWeight='lighter' color='#ffffff' textAlign='center' marginTop={10}>CloverScan is lightning fast, secure and powerful</Text>
              <SearchContainer>
                <SearchInput placeholder='Search by Address / Txn Hash /Token' onChange={this.changeSearchStr} />
                <SearchButton onClick={this.searchDapps}><img width={15} height={15} src={Search} alt=""/></SearchButton>
              </SearchContainer>
            </ContentContainer>
          </ContentWrapper>
          <BodyWrapper gap={'0'}>
            <div style={{position: 'relative', height: '100%'}}>
              <ContentTop>
                wer
              </ContentTop>
              {/*<Content>*/}
              {/*  wer*/}
              {/*</Content>*/}
            </div>
          </BodyWrapper>
        </AppBody>
    );
  }
}
