import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import { AutoColumn } from '../../components/Column'
import Row from "../../components/Row";

const BodyWrapper = styled(AutoColumn)`
`;

// const BodyContent = styled(AutoColumn)`
//   width: 1280px;
//   margin: 0 auto;
//
// `;

const HeadContainer = styled(Row)`
  padding: 0 20px 20px;
  background-color: #ffffff;
  position: absolute;
  width: 100%;
  left: 0;
`;

const MyDApps = styled.div`
  font-size: 40px;
  font-weight: bold;
  color: #333333;
  padding-left: 20px;
`;

const IframeContainer = styled.div`
  width: 100%;
  height: calc(100vh - 115px);
`;

export default class DAppsDetailWebComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
    }

    render() {
        const iframeUrl = this.props.location.state ? this.props.location.state.url : null;
        const dappName = this.props.location.state ? this.props.location.state.dappName : 'DApps Detail';
        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <MyDApps>{dappName}</MyDApps>
                    </HeadContainer>
                    <IframeContainer>
                        {iframeUrl ? <iframe
                            src={iframeUrl}
                            width="100%"
                            height="100%"
                            frameBorder='none'
                        ></iframe> : null}
                    </IframeContainer>
                </BodyWrapper>
            </AppBody>
        );
    }
}
