import React, { Component } from 'react';
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, { RowBetween, RowFit, RowCenter } from '../../components/Row'
import ImgDappExchange from '../../assets/images/polkadot_exchange.svg'
import Application from '../../assets/images/application.svg'
import SearchIcon from '../../assets/images/search_icon.svg'
import SwapIcon from '../../assets/images/swap_icon.svg'
import LendingIcon from '../../assets/images/lending_icon.svg'
import AggregationIcon from '../../assets/images/aggregation_icon.svg'
import AuctionIcon from '../../assets/images/auction_icon.svg'
import InsuranceIcon from '../../assets/images/insurance_icon.svg'
import TransferIcon from '../../assets/images/transfer_icon.svg'
import TabBottom from '../../assets/images/tab_bottom.svg'
import SwipeableViews from 'react-swipeable-views';
import Select from '@material-ui/core/Select';
import withStyles from '@material-ui/core/styles/withStyles';
import InputBase from '@material-ui/core/InputBase';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import MenuItem from '@material-ui/core/MenuItem';

const BodyWrapper = styled(AutoColumn)`
  height: calc(100vh - 50px);
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
  height: 44px;
`;

const DAppIconListTitle = styled(RowBetween)`
  margin-top: 10px;
  padding: 0 20px;
  margin-left: 5px;
`;

const DAppIconListWrapper = styled(RowBetween)`
  margin-top: 15px;
  padding: 0 20px;
`;

const DappIconWrapper = styled.div`
  text-align: center;
  flex-grow: 1;
  width: 50px;
`;

const DappIconName = styled(Text)`
  font-size: 12px;
  color: #666666;
  margin-top: 5px;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const SwapContainer = styled.div`
  margin: 15px 20px 0;
  padding: 15px 20px;
  background: rgba(246, 136, 59, 0.08);
  border-radius: 15px;
`;

const TransferWrapper = styled(Row)`
    position: relative;
`;

const TransferImg = styled.img`
  width: 35px;
  height: 35px;
  margin: 0 10px;
`

const SecondTitle = styled(Text)`
  font-size: 18px;
  color: #333333;
  font-weight: 600;
`;

const GrayBackground = styled.div`
  width: 100%;
  overflow-x: hidden;
  height: fit-content;
  background: linear-gradient(180deg, #FFFFFF 0%, #F3f3f3 100%);
`;

const SwapButton = styled(RowCenter)`
  padding: 0 10px;
  height: 35px;
  background: #FF7D30;
  border-radius: 18px;
  color: #ffffff;
  margin-left: 10px;
  position: absolute;
  right: 0;
  top: 0;
`;

const Search = styled.img`
  margin-left: 20px;
  width: 27px;
  height: 27px;
`;

const DappListWrapper = styled.div`
  background-color: #ffffff;
  margin: 0 20px;
  border-radius: 10px;
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  margin-top: 10px;
