import React, { Component } from 'react';
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, { RowBetween, RowFit, RowCenter } from '../../components/Row'
import ImgSearch from '../../assets/images/search.svg'
import ImgDappExchange from '../../assets/images/polkadot_exchange.svg'
import ImgDappData from '../../assets/images/polkadot_data.svg'
import Application from '../../assets/images/application.svg'
import SearchIcon from '../../assets/images/search_icon.svg'
import SwapIcon from '../../assets/images/swap_icon.svg'
import LendingIcon from '../../assets/images/lending_icon.svg'
import AggregationIcon from '../../assets/images/aggregation_icon.svg'
import AuctionIcon from '../../assets/images/auction_icon.svg'
import InsuranceIcon from '../../assets/images/insurance_icon.svg'

const BodyWrapper = styled(AutoColumn)`
  padding-left: 20px;
  padding-right: 20px;
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
`;

const DAppListTitle = styled(RowBetween)`
  margin-top: 10px;
`;

const DAppListWrapper = styled(RowBetween)`
  margin-top: 15px;
`;

const DappWrapper = styled.div`
  text-align: center;
`;

const SecondTitle = styled(Text)`
  font-size: 18px;
  color: #333333;
  font-weight: 600;
`;

const GrayBackground = styled.div`
  background: linear-gradient(94.54deg, #FF8212 0%, #ED4454 100%);
`;

const SwapButton = styled(RowCenter)`
  width: 75px;
  height: 30px;
  background: rgba(251, 130, 42, 0.1);
  border-radius: 30px;
`;

const MenuWrapper = styled.img`
  margin-left: 20px;
`;

const Search = styled.img`
  margin-left: 20px;
  width: 27px;
  height: 27px;
`;

const NavBar = styled(RowBetween)`
  margin-top: 15px;
  align-items: flex-start;
`;

const NavLink = styled(Text)`
  font-size: 16px;
  font-weight: 500;
  color: #41485D;
  text-decoration: none;
`;

const NavLinkActive = styled(NavLink)`
  color: #FB822A;
  padding-bottom: 3px;
  border-bottom: 2px solid #FB822A;
`;

const SearchContainer = styled.div`
  display: grid;
  grid-template-columns: auto 50px;

  margin: 10px 0px;
  background: #FAFAFA;
  border-radius: 36px;
`;

const SearchInput = styled.input`
  margin: 5px 0px 5px 10px;
  outline: none;
  border: none;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 16px;
  background: transparent;

  ::placeholder {
    color: rgba(65, 72, 93, 0.5);
  }
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  margin-top: 10px;
  margin-bottom: 10px;
`;

const DappContentWrapper = styled(Column)`
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #000000;
`;

const DappType = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #C4C4C4;
`;

// export default function Assets(): React.Component {
export default class DappM extends Component {
  constructor(props: {}, context: any) {
    super(props, context);
    this.state = {
      tabs: [
        {tabName:"Defi",id:1},
        {tabName:"Bright",id:2},
        {tabName:"Grant",id:3},
        {tabName:"Exange",id:4},
        {tabName:"NFT",id:5}
      ],
      currentIndex: 1,
    }
  }

  tabChoiced= (id: any) => {
    // tab切换的方法
    this.setState({
      currentIndex:id
    });
  }

  render() {
    // @ts-ignore
    const { tabs, currentIndex } = this.state;
    const recommendDapps = [
      {
        icon: SwapIcon,
        appName: 'Swap'
      },
      {
        icon: LendingIcon,
        appName: 'Lending'
      },
      {
        icon: AggregationIcon,
        appName: 'Aggregation'
      },
      {
        icon: AuctionIcon,
        appName: 'Auction'
      },
      {
        icon: InsuranceIcon,
        appName: 'Insurance'
      },
    ];
    return (
        <AppBody>
          <BodyWrapper gap={'md'}>
            <HeadContainer>
              <img width="148" height="18" src={Application} alt=""/>
              <Search src={SearchIcon} alt='search' />
              {/*<RowFit>*/}
                {/*<SwapButton>*/}
                {/*  <Text fontSize='15px' fontWeight='bold' color='#FB822A'>SWAP</Text>*/}
                {/*</SwapButton>*/}
                {/*<MenuWrapper src={ImgMenu} alt='menu' />*/}
              {/*</RowFit>*/}
            </HeadContainer>
            <div>
              <DAppListTitle>
                <SecondTitle>My DApps</SecondTitle>
                <Text color='#333333' fontSize='14px'>more</Text>
              </DAppListTitle>
            </div>
            <div>
              <DAppListTitle>
                <SecondTitle>Recommend DApps</SecondTitle>
              </DAppListTitle>
              <DAppListWrapper>
                {recommendDapps.map((dapp) => (
                    <DappWrapper>
                      <img width='50' height='50' src={dapp.icon} alt="" />
                      <Text fontSize='12px' color='#666666' marginTop='5px'>{dapp.appName}</Text>
                    </DappWrapper>
                ))}
              </DAppListWrapper>
            </div>
            <div>
              <DAppListTitle>
                <SecondTitle>Swap</SecondTitle>
              </DAppListTitle>

            </div>

            <GrayBackground>
              <NavBar>
                {tabs.map((res: any, index: number) => (
                    res.id === currentIndex ? <NavLinkActive>{res.tabName}</NavLinkActive> : (
                        <NavLink onClick={() => { this.tabChoiced(res.id); }}>{res.tabName}</NavLink>
                    ))
                )}
              </NavBar>

              {/*<NavBar>*/}
              {/*  <NavLinkActiveContainer><NavLinkActive>ALL</NavLinkActive></NavLinkActiveContainer>*/}
              {/*  <NavLink>Exchange</NavLink>*/}
              {/*  <NavLink>Tool</NavLink>*/}
              {/*  <NavLink>Source</NavLink>*/}
              {/*</NavBar>*/}

              {/*<SearchContainer>*/}
              {/*  <SearchInput placeholder='Search for Dapps' />*/}
              {/*  <img src={ImgSearch} width='50' height='50' alt='Search' />*/}
              {/*</SearchContainer>*/}

              <DappContainer>
                <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                <DappContentWrapper>
                  <Column>
                    <DappName>CLV Mining</DappName>
                    <DappDescription>Clover Mining Platform</DappDescription>
                  </Column>
                  {/*<DappType>Polkadot / Exchange</DappType>*/}
                </DappContentWrapper>
              </DappContainer>

              <DappContainer>
                <img height="50" width="50" src={ImgDappData} alt='Polkadot Data' />
                <DappContentWrapper>
                  <Column>
                    <DappName>CLV Mining</DappName>
                    <DappDescription>Clover Mining Platform</DappDescription>
                  </Column>
                  {/*<DappType>Polkadot / Data</DappType>*/}
                </DappContentWrapper>
              </DappContainer>

              <DappContainer>
                <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                <DappContentWrapper>
                  <Column>
                    <DappName>CLV Mining</DappName>
                    <DappDescription>Clover Mining Platform</DappDescription>
                  </Column>
                  {/*<DappType>Polkadot / Exchange</DappType>*/}
                </DappContentWrapper>
              </DappContainer>
            </GrayBackground>

          </BodyWrapper>
        </AppBody>
    );
  }

}
