import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import { AutoColumn } from '../../components/Column'
import Row from "../../components/Row";
import BackIcon from '../../assets/images/back_icon.svg'
import {Text} from "rebass";

const BodyWrapper = styled.div`
  width: 100%;
  background: linear-gradient(180deg, #FFFFFF 0%, #FFFFFF 46%, #F5F5F5 100%);
`;

const BodyContent = styled(AutoColumn)`
  width: 1280px;
  //width: 100%;
  margin: 0 auto;
`;

const HeadContainer = styled(Row)`
  padding: 0 20px 20px;
  background-color: #ffffff;
  position: absolute;
  width: 100%;
  left: 0;
`;

const MyDApps = styled.div`
  font-size: 40px;
  font-weight: bold;
  color: #333333;
  width: 1280px;
  margin: 0 auto;
  padding-left: 20px;
`;

const IframeContainer = styled.div`
  width: 100%;
  height: calc(100vh - 120px);
`;

export default class DAppsDetailWebComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
    }

    goBack = () => {
        this.props.history.replace('/dappm');
    }

    render() {
        const iframeUrl = this.props.location.state ? this.props.location.state.url : null;
        const dappName = this.props.location.state ? this.props.location.state.dappName : 'DApps Detail';
        return (
            <AppBody>
                <BodyWrapper>
                    <BodyContent gap={'md'}>
                        {/*<HeadContainer>*/}
                        {/*    <img onClick={this.goBack} src={BackIcon} alt="" width='9px' height='16px' />*/}
                        {/*    <MyDApps>{dappName}</MyDApps>*/}
                        {/*</HeadContainer>*/}
                        <HeadContainer>
                            <MyDApps>{dappName}</MyDApps>
                        </HeadContainer>
                        <IframeContainer>
                            {iframeUrl ? <iframe
                                src={iframeUrl}
                                width="100%"
                                height="100%"
                                frameBorder='none'
                            ></iframe> : null}
                        </IframeContainer>
                    </BodyContent>
                </BodyWrapper>
            </AppBody>
        );
    }
}
