import React, {Component} from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn, ColumnCenter } from '../../components/Column'
import Row, {RowBetween, RowEnd} from '../../components/Row'
import CloseBtn from '../../assets/images/closeBtn.svg'
import { ResponsivePie } from '@nivo/pie';
import { ResponsiveLine } from '@nivo/line';

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
  height: 44px;
`;

export default class AssetsMDetail extends Component<any, any> {

    goBack = () => {
        this.props.history.goBack();
    }
    render() {
        const pieData = [
            {
                "id": "Total assets",
                "label": "Total assets",
                "value": 75,
                "color": "#FF7D30"
            },
            {
                "id": "Total liabilities",
                "label": "Total liabilities",
                "value": 25,
                "color": "#FF589D"
            },
        ];

        const pieInnerStyles = {
            root: {
                fontFamily: "consolas, sans-serif",
                textAlign: "center",
                position: "relative",
                width: 600,
                height: 600,
            },
            overlay: {
                height: 110,
                width: 110,
                borderRadius: '50%',
                display: 'flex',
                flexDirection: 'column' as 'column',
                justifyContent: 'center' as 'center',
                backgroundColor: '#ffffff',
                boxShadow: '0px 5px 10px 2px rgba(255, 125, 48, 0.15)',
                position: "absolute" as "absolute",
                top: 200,
                right: 133,
                bottom: 0,
                alignItems: "center" as "center",
            },
            number: {
                color: "#333333",
                fontSize: "15px",
                fontWeight: 'bold' as 'bold'
            },
            text: {
                color: "#999999",
                fontSize: "12px",
                fontWeight: "normal" as "normal"
            }
        };

        const lineData = [
            {
                "id": "TotalAssets",
                "color": "#F6883B",
                "data": [
                    {
                        "x": "2020.08",
                        "y": 400.00
                    },
                    {
                        "x": "2020.08.10",
                        "y": 700.00
                    },
                    {
                        "x": "2020.08.20",
                        "y": 450.00
                    },
                    {
                        "x": "2020.09",
                        "y": 790.00
                    },
                    {
                        "x": "2020.09.10",
                        "y": 600.00
                    },
                    {
                        "x": "2020.09.20",
                        "y": 500.00
                    },
                    {
                        "x": "2020.10",
                        "y": 600.00
                    },
                    {
                        "x": "2020.10.10",
                        "y": 800.00
                    },
                    {
                        "x": "2020.10.20",
                        "y": 990.00
                    },
                    {
                        "x": "2020.11",
                        "y": 750.00
                    },
                    {
                        "x": "2020.11.10",
                        "y": 800.00
                    },
                ]
            },
            {
                "id": "NetAssets",
                "color": "#FF71C5",
                "data": [
                    {
                        "x": "2020.08",
                        "y": 200.00
                    },
                    {
                        "x": "2020.08.10",
                        "y": 500.00
                    },
                    {
                        "x": "2020.08.20",
                        "y": 350.00
                    },
                    {
                        "x": "2020.09",
                        "y": 590.00
                    },
                    {
                        "x": "2020.09.10",
                        "y": 400.00
                    },
                    {
                        "x": "2020.09.20",
                        "y": 300.00
                    },
                    {
                        "x": "2020.10",
                        "y": 400.00
                    },
                    {
                        "x": "2020.10.10",
                        "y": 600.00
                    },
                    {
                        "x": "2020.10.20",
                        "y": 790.00
                    },
                    {
                        "x": "2020.11",
                        "y": 550.00
                    },
                    {
                        "x": "2020.11.10",
                        "y": 600.00
                    },
                ]
            },
            {
                "id": "Total liabilities",
                "color": "#406AFE",
                "data": [
                    {
                        "x": "2020.08",
                        "y": 100.00
                    },
                    {
                        "x": "2020.08.10",
                        "y": 400.00
                    },
                    {
                        "x": "2020.08.20",
                        "y": 150.00
                    },
                    {
                        "x": "2020.09",
                        "y": 490.00
                    },
                    {
                        "x": "2020.09.10",
                        "y": 300.00
                    },
                    {
                        "x": "2020.09.20",
                        "y": 200.00
                    },
                    {
                        "x": "2020.10",
                        "y": 300.00
                    },
                    {
                        "x": "2020.10.10",
                        "y": 500.00
                    },
                    {
                        "x": "2020.10.20",
                        "y": 690.00
                    },
                    {
                        "x": "2020.11",
                        "y": 450.00
                    },
                    {
                        "x": "2020.11.10",
                        "y": 500.00
                    },
                ]
            },
        ];

        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <Text fontSize='23px' fontWeight='bold' color='#333333'>Assets</Text>
                        <img src={CloseBtn} alt="" width='15px' height='15px' onClick={this.goBack} />
                    </HeadContainer>
                    <Text fontSize='18px' fontWeight='500' color='#333333' marginLeft='25px'>Assets distribution</Text>
                    <div style={{height: 250}}>
                        <ResponsivePie
                            data={pieData}
                            margin={{ top: 20, right: 0, bottom: 50, left: 0 }}
                            startAngle={1}
                            innerRadius={0.88}
                            padAngle={5}
                            cornerRadius={5}
                            colors={['#FF7D30', '#FF589D']}
                            borderWidth={1}
                            borderColor={{ from: 'color', modifiers: [ [ 'darker', 0.2 ] ] }}
                            radialLabelsSkipAngle={10}
                            radialLabelsTextColor="#333333"
                            radialLabelsLinkColor={{ from: 'color' }}
                            sliceLabelsSkipAngle={10}
                            sliceLabelsTextColor="#333333"
                            enableRadialLabels={false}
                            enableSliceLabels={false}
                            legends={[
                                {
                                    anchor: 'bottom',
                                    direction: 'row',
                                    justify: false,
                                    translateX: 0,
                                    translateY: 40,
                                    itemsSpacing: 20,
                                    itemWidth: 100,
                                    itemHeight: 18,
                                    itemTextColor: '#999',
                                    itemDirection: 'left-to-right',
                                    itemOpacity: 1,
                                    symbolSize: 12,
                                    symbolShape: 'circle',
                                    effects: [
                                        {
                                            on: 'hover',
                                            style: {
                                                itemTextColor: '#000',
                                            }
                                        }
                                    ]
                                }
                            ]}
                        />
                        <div style={pieInnerStyles.overlay}>
                            <span style={pieInnerStyles.number}>$234995.300</span>
                            <span style={pieInnerStyles.text}>Net assets</span>
                        </div>
                    </div>

                    <Text fontSize='18px' fontWeight='500' color='#333333' marginLeft='25px'>Assets trend</Text>
                    <div style={{height: 300}}>
                        <ResponsiveLine
                            data={lineData}
                            margin={{ top: 50, right: 30, bottom: 50, left: 30 }}
                            xScale={{ type: 'point' }}
                            yScale={{ type: 'linear', min: 'auto', max: 'auto', stacked: true, reverse: false }}
                            // yFormat=" >-.2f"
                            axisTop={null}
                            axisRight={null}
                            axisLeft={null}
                            // axisBottom={null}
                            axisBottom={{
                                orient: 'bottom',
                                tickSize: 0,
                                tickPadding: 5,
                                tickRotation: 0,
                                tickValues: ["2020.08", "2020.09", "2020.10", "2020.11"]
                            }}
                            enableArea={true}
                            enableGridX={false}
                            enableGridY={false}
                            curve='monotoneX'
                            pointSize={2}
                            colors={['#F6883B', '#FF71C5', '#406AFE']}
                            pointColor={{ from: 'serieColor' }}
                            pointBorderWidth={2}
                            pointBorderColor={{ from: 'serieColor' }}
                            // pointLabelYOffset={-12}
                            useMesh={true}
                            defs={[
                                {
                                    id: 'gradientA',
                                    type: 'linearGradient',
                                    colors: [
                                        { offset: 0, color: '#F6883B' },
                                        { offset: 80, color: '#ffffff' },
                                    ],
                                },
                                {
                                    id: 'gradientB',
                                    type: 'linearGradient',
                                    colors: [
                                        { offset: 0, color: '#FF71C5' },
                                        { offset: 80, color: '#ffffff' },
                                    ],
                                },
                                {
                                    id: 'gradientC',
                                    type: 'linearGradient',
                                    colors: [
                                        { offset: 0, color: '#406AFE' },
                                        { offset: 80, color: '#ffffff' },
                                    ],
                                },
                            ]}
                            fill={[
                                { match: d => d.id === 'TotalAssets', id: 'gradientA' },
                                { match: d => d.id === 'NetAssets', id: 'gradientB' },
                                { match: d => d.id === 'Total liabilities', id: 'gradientC' },
                            ]}
                            legends={[
                                {
                                    anchor: 'top',
                                    direction: 'row',
                                    justify: false,
                                    translateX: 0,
                                    translateY: -30,
                                    itemsSpacing: 20,
                                    itemDirection: 'left-to-right',
                                    itemWidth: 100,
                                    itemHeight: 20,
                                    symbolSize: 12,
                                    symbolShape: 'circle',
                                    symbolBorderColor: 'rgba(0, 0, 0, .5)',
                                    effects: [
                                        {
                                            on: 'hover',
                                            style: {
                                                itemBackground: 'rgba(0, 0, 0, .03)',
                                                itemOpacity: 1
                                            }
                                        }
                                    ]
                                }
                            ]}
                        />
                    </div>
                </BodyWrapper>
            </AppBody>
        );
    }
}
