import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row from "../../components/Row";
import {Text} from "rebass";
import ImgDappExchange from "../../assets/images/polkadot_exchange.svg";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import SwipeableViews from "react-swipeable-views";
import withStyles from "@material-ui/core/styles/withStyles";

const BodyWrapper = styled.div`
  width: 100%;
  background: #F5F5F5;
`;

const BodyContent = styled(AutoColumn)`
  width: 1024px;
  margin: 0 auto;
`;


const HeadContainer = styled(Row)`
  padding: 20px 20px;
  background-color: #ffffff;
  position: absolute;
  width: 100%;
  left: 0;
`;

const TitleText = styled.div`
  font-size: 28px;
  font-weight: bold;
  color: #333333;
  width: 1024px;
  margin: 0 auto;
  padding-left: 20px;
`;

const GrayBackground = styled.div`
  width: 100%;
  overflow: hidden;
  margin-top: 70px;
`;

const DappContainer = styled.div`
  cursor: pointer;
  display: inline-block;
  width: 499px;
  height: 119px;
  background: #ffffff;
  border-radius: 20px;
  padding: 20px 30px;
  margin-bottom: 20px;
  border: 1px solid #ffffff;
  :nth-child(odd) {
    margin-right: 20px;
  }
  :hover {
    border-color: #FF7D30;
  }
`;

const DappContentWrapper = styled(Column)`
  display: inline-block;
  vertical-align: middle;
  max-width: 75%;
  margin-left: 20px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 18px;
  padding-bottom: 10px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 14px;
  font-weight: normal;
  color: #666666;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

export default class MoreDAppsWebComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            tabList: [
                {tabName:"Recent",id:1},
                {tabName:"Favorites",id:2},
            ],
            currentIndex: 0,
        }
    }

    goDappsDetail = (url: any, dappName: string) => {
        this.props.history.push({pathname: '/dappsdetail', state: {url: url, dappName: dappName}});
    }

    handleChange = (event: any, value: any) => {
        this.setState({
            currentIndex: value,
        });
    };

    handleChangeIndex = (index: any) => {
        this.setState({
            currentIndex: index,
        });
    };

    render() {
        const title = this.props.location.state ? this.props.location.state.title : 'My DApps';
        const { tabList, currentIndex } = this.state;
        const dapplist = [
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            }
        ];
        const slideStyles = {
            tabs: {
                height: '65px',
                margin: '40px 0 20px 0',
            },
            slide: {
                padding: '10px 0',
                minHeight: 'fit-content',
                color: '#fff',
                overflow: 'hidden'
            }
        };
        const AntTab = withStyles((theme) => ({
            root: {
                textTransform: 'none',
                fontSize: '24px',
                '&$selected': {
                    color: '#FF7D30',
                    fontWeight: theme.typography.fontWeightMedium,
                },
            },
            selected: {},
        }))(Tab);
        const StyledTabs = withStyles({
            indicator: {
                display: 'flex',
                justifyContent: 'center',
                backgroundColor: 'transparent',
                '& > span': {
                    height: '8px',
                    width: '34px',
                    marginTop: '-8px',
                    backgroundColor: 'rgba(246, 136, 59, 1)',
                    borderRadius: '4px',
                },
            },
        })((props) => <Tabs {...props} value={currentIndex} onChange={this.handleChange} style={slideStyles.tabs} TabIndicatorProps={{ children: <span /> }} />);

        return (
            <AppBody>
                <BodyWrapper>
                    <BodyContent gap={'0px'}>
                        <HeadContainer>
                            <TitleText>{title}</TitleText>
                        </HeadContainer>
                        <GrayBackground>
                            <StyledTabs>
                                {tabList.map((res: any) => (
                                    <AntTab key={res.tabName} label={res.tabName} />
                                ))}
                            </StyledTabs>
                            <SwipeableViews index={currentIndex} onChangeIndex={this.handleChangeIndex} style={{ height: '100%' }} enableMouseEvents>
                                {tabList.map(() => (
                                    <div style={slideStyles.slide}>
                                        {dapplist.map((dapp: any) => (
                                            <DappContainer onClick={() => { this.goDappsDetail(dapp.url, dapp.dappName); }}>
                                                <img height="90" width="90" src={ImgDappExchange} alt='Polkadot Exchange' />
                                                <DappContentWrapper>
                                                    <Column>
                                                        <DappName>{dapp.dappName}</DappName>
                                                        <DappDescription>{dapp.describe}</DappDescription>
                                                    </Column>
                                                </DappContentWrapper>
                                            </DappContainer>
                                        ))}
                                    </div>
                                ))}
                            </SwipeableViews>
                        </GrayBackground>
                    </BodyContent>
                </BodyWrapper>
            </AppBody>
        );
    }
}
