import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row from "../../components/Row";
import BackIcon from '../../assets/images/back_icon.svg'
import {Text} from "rebass";
import ImgDappExchange from "../../assets/images/polkadot_exchange.svg";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import SwipeableViews from "react-swipeable-views";
import withStyles from "@material-ui/core/styles/withStyles";

const BodyWrapper = styled.div`
  width: 100%;
  background: #F5F5F5;
`;

const BodyContent = styled(AutoColumn)`
  width: 1280px;
  margin: 0 auto;
`;


const HeadContainer = styled(Row)`
  padding: 0 20px;
  margin-top: 50px;
  height: 44px;
  background-color: #ffffff;
`;

const MyDApps = styled(Text)`
  font-size: 20px;
  font-weight: bold;
  color: #333333;
  text-align: center;
  width: 100%;
`;

const GrayBackground = styled.div`
  width: 100%;
  overflow: hidden;
  //background-color: #F7F7F7;
  height: calc(100vh - 144px);
`;

const DappListWrapper = styled.div`
  //margin: 0 20px;
  //border-radius: 10px;
`;

const DappContainer = styled.div`
  cursor: pointer;
  display: inline-block;
  width: 620px;
  height: 140px;
  background: #ffffff;
  border-radius: 30px;
  padding: 25px 30px;
  margin-bottom: 40px;
  :nth-child(odd) {
    margin-right: 40px;
  }
`;

const DappContentWrapper = styled(Column)`
  display: inline-block;
  vertical-align: middle;
  max-width: 75%;
  margin-left: 26px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 24px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 20px;
  font-weight: normal;
  color: #666666;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;


export default class MoreDAppsWebComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            tabList: [
                {tabName:"Recent",id:1},
                {tabName:"Favorites",id:2},
            ],
            currentIndex: 0,
        }
    }
    goBack = () => {
        this.props.history.goBack();
    }

    goDappsDetail = (url: any, dappName: string) => {
        this.props.history.push({pathname: '/dappsdetail', state: {url: url, dappName: dappName}});
    }

    handleChange = (event: any, value: any) => {
        this.setState({
            currentIndex: value,
        });
    };

    handleChangeIndex = (index: any) => {
        this.setState({
            currentIndex: index,
        });
    };

    render() {
        const { tabList, currentIndex } = this.state;
        const dapplist = [
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            }
        ];
        const slideStyles = {
            tabs: {
                padding: '0 20px',
            },
            slide: {
                padding: '10px 0',
                minHeight: 'fit-content',
                color: '#fff',
                overflow: 'hidden'
            }
        };
        const AntTab = withStyles((theme) => ({
            root: {
                textTransform: 'none',
                fontSize: '15px',
                '&$selected': {
                    color: '#FF7D30',
                    fontWeight: theme.typography.fontWeightMedium,
                },
            },
            selected: {},
        }))(Tab);
        return (
            <AppBody>
                <BodyWrapper>
                    <BodyContent gap={'0px'}>
                        <HeadContainer>
                            <img onClick={this.goBack} src={BackIcon} alt="" width='9px' height='16px' />
                            <MyDApps>My DApps</MyDApps>
                        </HeadContainer>

                        <GrayBackground>
                            <Tabs value={currentIndex} onChange={this.handleChange} style={slideStyles.tabs}
                                  TabIndicatorProps={{
                                      style: {
                                          height: '4px',
                                          width: '17px',
                                          backgroundColor: 'rgba(246, 136, 59, 1)',
                                          borderRadius: '2px',
                                          marginLeft: '8%'
                                      }
                                  }}
                            >
                                {tabList.map((res: any) => (
                                    <AntTab label={res.tabName}/>
                                ))}
                            </Tabs>
                            <DappListWrapper>
                                <SwipeableViews index={currentIndex} onChangeIndex={this.handleChangeIndex} style={{ height: '100%' }} enableMouseEvents>
                                    {tabList.map(() => (
                                        <div style={slideStyles.slide}>
                                            {dapplist.map((dapp: any) => (
                                                <DappContainer onClick={() => { this.goDappsDetail(dapp.url, dapp.dappName); }}>
                                                    <img height="90" width="90" src={ImgDappExchange} alt='Polkadot Exchange' />
                                                    <DappContentWrapper>
                                                        <Column>
                                                            <DappName>{dapp.dappName}</DappName>
                                                            <DappDescription>{dapp.describe}</DappDescription>
                                                        </Column>
                                                    </DappContentWrapper>
                                                </DappContainer>
                                            ))}
                                        </div>
                                    ))}
                                </SwipeableViews>
                                {/*<SwipeableViews index={currentIndex} onChangeIndex={this.handleChangeIndex}>*/}
                                {/*    {tabList.map(() => (*/}
                                {/*        <div style={slideStyles.slide}>*/}
                                {/*            {dapplist.map((dapp: any) => (*/}
                                {/*                <DappContainer onClick={() => { this.goDappsDetail(dapp.url); }}>*/}
                                {/*                    <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />*/}
                                {/*                    <DappContentWrapper>*/}
                                {/*                        <Column>*/}
                                {/*                            <DappName>{dapp.dappName}</DappName>*/}
                                {/*                            <DappDescription>{dapp.describe}</DappDescription>*/}
                                {/*                        </Column>*/}
                                {/*                    </DappContentWrapper>*/}
                                {/*                </DappContainer>*/}
                                {/*            ))}*/}
                                {/*        </div>*/}
                                {/*    ))}*/}
                                {/*</SwipeableViews>*/}
                            </DappListWrapper>
                        </GrayBackground>
                    </BodyContent>
                </BodyWrapper>
            </AppBody>
        );
    }
}
