            let x = null
            let y = null
            if (datum.y !== null && datum.y !== undefined) {
                x = xScale(datum.x)
                y = yScale(datum.y)
            }
            const point = {
                id: `${rawSerie.id}.${i}`,
                serie: rawSerie,
                data: datum,
                x,
                y,
