import React, { memo } from 'react'
import PropTypes from 'prop-types'
import { useSprings, animated } from 'react-spring'
import { useTheme, useMotionConfig } from '@nivo/core'
import { inheritedColorPropType } from '@nivo/colors'
import { useSeriesLabels } from './hooks'

const AreasLabels = ({ series, position, padding, color }) => {
    const theme = useTheme()
    const { animate, config: springConfig } = useMotionConfig()

    const labels = useSeriesLabels({
        series,
        position,
        padding,
        color,
    })

    const springs = useSprings(
        labels.length,
        labels.map(label => ({
            x: label.x,
            y: label.y,
            opacity: label.opacity,
            config: springConfig,
            immediate: !animate,
        }))
    )

    return springs.map((animatedProps, index) => {
        const label = labels[index]

        return (
            <animated.text
                key={label.id}
                x={animatedProps.x}
                y={animatedProps.y}
                textAnchor={label.textAnchor}
                dominantBaseline="central"
                opacity={animatedProps.opacity}
                style={{
                    ...theme.labels.text,
                    fill: label.color,
                }}
            >
                {label.id}
            </animated.text>
        )
    })
}

AreasLabels.propTypes = {
    series: PropTypes.arrayOf(
        PropTypes.shape({
            id: PropTypes.string.isRequired,
            data: PropTypes.arrayOf(
                PropTypes.shape({
                    x: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
                    y: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
                })
            ).isRequired,
        })
    ).isRequired,
    position: PropTypes.oneOf(['start', 'end']).isRequired,
    padding: PropTypes.number.isRequired,
    color: inheritedColorPropType.isRequired,
}

export default memo(AreasLabels)
