import React, {Component} from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn, ColumnCenter } from '../../components/Column'
import PieChartBackground from "../../assets/images/pie_chart_background.svg";
import { ResponsiveContainer, PieChart, Pie, Sector, Cell, AreaChart, Area, BarChart, Bar, LabelList, XAxis, ReferenceLine, Tooltip, Legend } from 'recharts';

const BodyWrapper = styled.div`
  width: 100%;
`;

const BodyContent = styled(AutoColumn)`
  width: 1280px;
  margin: 0 auto;
`;

const PieChartWrapper = styled.div`
  margin: 30px auto;
  height: 470px;
  width: 1100px;
  position: relative;
  background: url('${PieChartBackground}') no-repeat 100% 100%;
`;

const LineChartWrapper = styled.div`
  margin: 30px auto;
  height: 550px;
  width: 1100px;
  background: linear-gradient(180deg,#FFFFFF 0%, #FFF6FA 50%, #FFFFFF 100%);
`;

const BarChartWrapper = styled.div`
  position: relative;
  margin: 30px auto;
  height: 470px;
  width: 1100px;
  background: linear-gradient(180deg, #FFFFFF 0%, #EFFBFF 50%, #FFFFFF 100%);
`;

export default class AssetsMDetailsWeb extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            activeIndex: null
        }
    }

    onPieEnter = (data: any, index: number) => {
        console.log(data);
        console.log(index);
        this.setState({
            activeIndex: index,
        });
    };
    render() {
        const pieData = [
            {
                id: "Total assets",
                name: "Total assets",
                value: 75,
                color: "#FF7D30"
            },
            {
                id: "Total liabilities",
                name: "Total liabilities",
                value: 25,
                color: "#FF589D"
            },
        ];
        const pieInnerStyles = {
            root: {
                fontFamily: "consolas, sans-serif",
                textAlign: "center",
                position: "relative",
                width: 600,
                height: 600,
            },
            overlay: {
                height: 252,
                width: 252,
                borderRadius: '50%',
                display: 'flex',
                flexDirection: 'column' as 'column',
                justifyContent: 'center' as 'center',
                backgroundColor: '#ffffff',
                boxShadow: '0px 10px 20px 4px rgba(255, 125, 48, 0.15)',
                position: "absolute" as "absolute",
                zIndex: 1,
                top: '50%',
                marginTop: '-140px',
                right: '50%',
                marginRight: '26px',
                bottom: 0,
                alignItems: "center" as "center",
            },
            number: {
                color: "#333333",
                fontSize: "28px",
                fontWeight: 'bold' as 'bold'
            },
            text: {
                color: "#999999",
                fontSize: "20px",
                fontWeight: "normal" as "normal"
            }
        };
        const renderActiveTooltipPie = (props: any) => {
            const { payload } = props;
            return (
                payload[0]? <div
                    key={payload[0].name}
                    style={{
                        color: '#999999',
                        fontSize: '20px',
                        backgroundColor: '#ffffff',
                        borderRadius: '10px',
                        padding: '6px 20px',
                        boxShadow: '0px 0px 20px 4px rgba(0, 0, 0, 0.15)',
                    }}
                >
                        <span style={{
                            display: 'inline-block',
                            marginRight: '10px',
                            width: '16px',
                            height: '16px',
                            borderRadius: '8px',
                            background: payload[0].payload.color
                        }}></span>
                    {payload[0].name}
                    <span style={{color: '#333333', marginLeft: '10px', fontSize: '26px'}}>${payload[0].value}</span>
                </div> : <div></div>
            );
        };
        const renderLegendPie = (props: any) => {
            const { payload } = props;
            return (
                <div style={{marginRight: '150px'}}>
                    {
                        payload.map((entry: any, index: number) => (
                            <div style={{marginBottom: '20px'}}>
                                <span style={{
                                    display: 'inline-block',
                                    marginRight: '10px',
                                    width: '16px',
                                    height: '16px',
                                    borderRadius: '8px',
                                    background: entry.color
                                }}></span>
                                <span style={{color: '#666666', fontSize: '20px'}}>{entry.value}</span>
                            </div>
                        ))
                    }
                </div>
            );
        }
        const renderActiveShapePie = (props: any) => {
            const RADIAN = Math.PI / 180;
            const {
                cx, cy, midAngle, innerRadius, outerRadius, startAngle, endAngle, cornerRadius,
                fill, payload, percent, value,
            } = props;
            const sin = Math.sin(-RADIAN * midAngle);
            const cos = Math.cos(-RADIAN * midAngle);
            const sx = cx + (outerRadius + 10) * cos;
            const sy = cy + (outerRadius + 10) * sin;
            const mx = cx + (outerRadius + 30) * cos;
            const my = cy + (outerRadius + 30) * sin;
            const ex = mx + (cos >= 0 ? 1 : -1) * 22;
            const ey = my;
            const textAnchor = cos >= 0 ? 'start' : 'end';

            return (
                <g>
                    <text x={cx} y={cy} dy={8} textAnchor="middle" fill={fill}>{payload.name}</text>
                    <Sector
                        cx={cx}
                        cy={cy}
                        innerRadius={innerRadius + 10}
                        outerRadius={outerRadius + 10}
                        startAngle={startAngle}
                        endAngle={endAngle}
                        cornerRadius={cornerRadius}
                        fill={fill}
                    />
                    <path d={`M${sx},${sy}L${mx},${my}L${ex},${ey}`} stroke={fill} fill="none" />
                    <circle cx={ex} cy={ey} r={4} fill={fill} stroke="none" />
                    <text x={ex + (cos >= 0 ? 1 : -1) * 12} y={ey-5} textAnchor={textAnchor} fill="#333333">{payload.name}: ${value}</text>
                    <text x={ex + (cos >= 0 ? 1 : -1) * 12} y={ey} dy={18} textAnchor={textAnchor} fill="#999">
                        {`(Rate ${(percent * 100).toFixed(2)}%)`}
                    </text>
                </g>
            );
        };

        const lineData = [
            {
                "name": "2020.07.20",
                "Total liabilities": 100,
                "NetAssets": 200,
                "TotalAssets": 300
            },
            {
                "name": "2020.08",
                "Total liabilities": 200,
                "NetAssets": 300,
                "TotalAssets": 400
            },
            {
                "name": "2020.08.10",
                "Total liabilities": 300,
                "NetAssets": 400,
                "TotalAssets": 500
            },
            {
                "name": "2020.08.20",
                "Total liabilities": 200,
                "NetAssets": 300,
                "TotalAssets": 400
            },
            {
                "name": "2020.09",
                "Total liabilities": 100,
                "NetAssets": 200,
                "TotalAssets": 300
            },
            {
                "name": "2020.09.10",
                "Total liabilities": 300,
                "NetAssets": 400,
                "TotalAssets": 500
            },
            {
                "name": "2020.09.20",
                "Total liabilities": 400,
                "NetAssets": 500,
                "TotalAssets": 600
            },
            {
                "name": "2020.10",
                "Total liabilities": 200,
                "NetAssets": 300,
                "TotalAssets": 400
            },
            {
                "name": "2020.10.10",
                "Total liabilities": 400,
                "NetAssets": 500,
                "TotalAssets": 600
            },
            {
                "name": "2020.10.20",
                "Total liabilities": 500,
                "NetAssets": 600,
                "TotalAssets": 700
            },
            {
                "name": "2020.11",
                "Total liabilities": 800,
                "NetAssets": 700,
                "TotalAssets": 900
            },
            {
                "name": "2020.11.10",
                "Total liabilities": 400,
                "NetAssets": 500,
                "TotalAssets": 600
            }
        ]
        const renderActiveTooltipLine = (props: any) => {
            const { payload } = props;
            return (
                <div
                    style={{
                        background: 'white',
                        padding: '20px',
                        opacity: '0.92',
                        width: '270px',
                        height: '270px',
                        borderRadius: '20px',
                        boxShadow: '0px 0px 10px 4px rgba(0, 0, 0, 0.05)',
                    }}
                >
                    {payload.map((point: any) => (
                        <div
                            key={point.name}
                            style={{
                                color: '#999999',
                                padding: '6px 0',
                                fontSize: '20px'
                            }}
                        >
                            <div>
                                <span style={{
                                    display: 'inline-block',
                                    marginRight: '10px',
                                    width: '16px',
                                    height: '16px',
                                    borderRadius: '8px',
                                    background: point.stroke
                                }}></span>
                                {point.name}
                            </div>
                            <span style={{color: '#333333', marginLeft: '26px', fontSize: '24px'}}>${point.value}</span>
                        </div>
                    ))}
                </div>
            );
        };

        const barInnerStyles = {
            overlay: {
                width: 270,
                height: 60,
                display: 'flex',
                flexDirection: 'row' as 'row',
                position: "absolute" as "absolute",
                top: 0,
                right: 0,
            },
            span: {
                width: '6px',
                height: '100%',
                backgroundColor: '#FF7D30',
                marginRight: '10px'
            },
            divWrapper: {
                display: 'flex',
                flexDirection: 'column' as 'column',
            },
            number: {
                color: "#333333",
                fontSize: "24px",
            },
            text: {
                color: "#999999",
                textAlign: 'center' as 'center',
                fontSize: "20px",
                lineHeight: '28px',
            }
        };
        const barData = [
            {
                name: 'CLV', earnings: 25.03, color: '#FF7D30'
            },
            {
                name: 'cETH', earnings: 20.03, color: '#FF71C5'
            },
            {
                name: 'ACA', earnings: 15.03, color: '#406AFE'
            },
            {
                name: 'aUSD', earnings: 10.03, color: '#F6883B'
            },
            {
                name: 'bUSD', earnings: -10.03, color: '#FF71C5'
            },
            {
                name: 'cUSD', earnings: -15.03, color: '#FF7D30'
            }
        ];
        const BarLabel =(props: any) =>{
            const { x, y, width, height, value } = props;
            return (
                <text x={x + width / 2} y={value > 0 ? y - 30 : y + 30} fill="#666666" fontSize={20} textAnchor="middle" dominantBaseline="middle">
                    {value > 0 ? `+$${value}` : `-$${value * -1}`}
                </text>
            )
        }

        return (
            <AppBody>
                <BodyWrapper>
                    <BodyContent gap={'md'}>
                        <Text fontSize='30px' fontWeight='bold' color='#333333' marginLeft='25px'>Assets distribution</Text>
                        <PieChartWrapper>
                            <ResponsiveContainer width='100%'>
                                <PieChart
                                    margin={{ top: 0, right: 0, left: 0, bottom: 30 }}
                                >
                                    <Pie
                                        activeIndex={this.state.activeIndex}
                                        activeShape={renderActiveShapePie}
                                        onClick={this.onPieEnter}
                                        data={pieData}
                                        startAngle={90}
                                        endAngle={-270}
                                        cornerRadius={20}
                                        dataKey="value"
                                        innerRadius="77%"
                                        outerRadius="90%"
                                        paddingAngle={6}
                                    >
                                        {
                                            pieData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.color} />)
                                        }
                                    </Pie>
                                    <Tooltip wrapperStyle={{zIndex: 2}} content={renderActiveTooltipPie} />
                                    <Legend align={'right'} verticalAlign={'middle'} layout={'vertical'} content={renderLegendPie} />
                                </PieChart>
                            </ResponsiveContainer>
                            <div style={pieInnerStyles.overlay}>
                                <span style={pieInnerStyles.number}>$234995.300</span>
                                <span style={pieInnerStyles.text}>Net assets</span>
                            </div>
                        </PieChartWrapper>

                        <Text fontSize='30px' fontWeight='bold' color='#333333' marginLeft='25px'>Assets trend</Text>
                        <LineChartWrapper>
                            <ResponsiveContainer width='100%'>
                                <AreaChart data={lineData}
                                           margin={{ top: 10, right: 0, left: 0, bottom: 20 }}>
                                    <defs>
                                        <linearGradient id="colorLiabilities" x1="0" y1="0" x2="0" y2="1">
                                            <stop offset="5%" stopColor="#406AFE" stopOpacity={0.3}/>
                                            <stop offset="100%" stopColor="#ffffff" stopOpacity={0}/>
                                        </linearGradient>
                                        <linearGradient id="colorNetAssets" x1="0" y1="0" x2="0" y2="1">
                                            <stop offset="5%" stopColor="#FF71C5" stopOpacity={0.3}/>
                                            <stop offset="100%" stopColor="#ffffff" stopOpacity={0}/>
                                        </linearGradient>
                                        <linearGradient id="colorTotalAssets" x1="0" y1="0" x2="0" y2="1">
                                            <stop offset="5%" stopColor="#FF7D30" stopOpacity={0.3}/>
                                            <stop offset="100%" stopColor="#ffffff" stopOpacity={0}/>
                                        </linearGradient>
                                    </defs>
                                    <XAxis dataKey="name" axisLine={false} tickLine={false} ticks={["2020.08", "2020.09", "2020.10", "2020.11"]} tick={{fill: '#666666', fontSize: 18}} tickMargin={20} interval={0} />
                                    <Tooltip offset={40} cursor={{ stroke: '#eeeeee', strokeWidth: 1 }} content={renderActiveTooltipLine} wrapperStyle={{color: 'red'}} />
                                    <Area activeDot={{ stroke: '#FF7D30', fill: '#ffffff', strokeWidth: 4, r: 12 }} type="monotone" dataKey="TotalAssets" stroke="#FF7D30" strokeWidth={4} fillOpacity={1} fill="url(#colorTotalAssets)" />
                                    <Area activeDot={{ stroke: '#FF71C5', fill: '#ffffff', strokeWidth: 4, r: 12 }} type="monotone" dataKey="NetAssets" stroke="#FF71C5" strokeWidth={4} fillOpacity={1} fill="url(#colorNetAssets)" />
                                    <Area activeDot={{ stroke: '#406AFE', fill: '#ffffff', strokeWidth: 4, r: 12 }} type="monotone" dataKey="Total liabilities" stroke="#406AFE" strokeWidth={4} fillOpacity={1} fill="url(#colorLiabilities)" />
                                </AreaChart>
                            </ResponsiveContainer>
                        </LineChartWrapper>

                        <Text fontSize='30px' fontWeight='bold' color='#333333' marginLeft='25px'>Profit and loss of assets</Text>
                        <BarChartWrapper>
                            <ResponsiveContainer width='100%'>
                                <BarChart
                                    data={barData}
                                    margin={{
                                        top: 60, right: 40, left: 40, bottom: 20,
                                    }}
                                >
                                    <XAxis dx={-40} dataKey="name" axisLine={false} tickLine={false} tick={{fill: '#666666', fontSize: 18}} interval={0} />
                                    <ReferenceLine y={0} stroke="rgba(64, 106, 254, 0.12)" strokeWidth={1} />
                                    <Bar dataKey="earnings" fill="#8884d8" radius={[16, 16, 0, 0]}>
                                        <LabelList
                                            dataKey="earnings"
                                            position="top"
                                            content={<BarLabel />}
                                        />
                                        {
                                            barData.map((entry) => (
                                                <Cell fill={entry.color} width={50} />
                                            ))
                                        }
                                    </Bar>
                                </BarChart>
                            </ResponsiveContainer>
                            <div style={barInnerStyles.overlay}>
                                <span style={barInnerStyles.span}></span>
                                <div style={barInnerStyles.divWrapper}>
                                    <span style={barInnerStyles.text}>Earnings today</span>
                                    <span style={barInnerStyles.number}>$1254576.03</span>
                                </div>
                            </div>
                        </BarChartWrapper>
                    </BodyContent>
                </BodyWrapper>
            </AppBody>
        );
    }
}
