import React, {Component} from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn, ColumnCenter } from '../../components/Column'
import Row, {RowBetween, RowEnd} from '../../components/Row'
import CloseBtn from '../../assets/images/closeBtn.svg'
import { Pie, Line } from '@ant-design/charts';

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
  height: 44px;
`;

export default class AssetsMDetail extends Component<any, any> {

    goBack = () => {
        this.props.history.goBack();
    }
    render() {
        const pieData = [
            {
                type: 'Total assets',
                value: 80,
            },
            {
                type: 'Total liabilities',
                value: 20,
            },
        ];
        const pieConfig = {
            width: 250,
            height: 250,
            appendPadding: 10,
            data: pieData,
            angleField: 'value',
            colorField: 'type',
            color: ['#FF7D30', '#FF589D'],
            radius: 1,
            innerRadius: 0.85,
            label: {
                content: '',
            },
            legend: {
                layout: 'horizontal' as 'horizontal',
                position: 'bottom' as 'bottom'
            },
            interactions: [{ type: 'element-selected' }, { type: 'element-active' }],
            statistic: {
                title: false as false,
                content: {
                    style: {
                        whiteSpace: 'pre-wrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                    },
                    formatter: function formatter() {
                        return '<div style="height: 130px; width: 130px; border-radius: 50%; display: flex; flex-direction: column; justify-content: center; background-color: #ffffff; box-shadow: 0px 0px 10px 4px rgba(255, 125, 48, 0.15);">' +
                            '<div style="color: #333333; font-size: 15px;">$234995.300</div>' +
                            '<span style="color: #999999; font-size: 12px; font-weight: normal; margin-top: 5px;">Net assets</span>' +
                            '</div>';
                    },
                },
            },
        };

        const lineData = [
            {
                year: '1991',
                value: 3,
            },
            {
                year: '1992',
                value: 4,
            },
            {
                year: '1993',
                value: 3.5,
            },
            {
                year: '1994',
                value: 5,
            },
            {
                year: '1995',
                value: 4.9,
            },
            {
                year: '1996',
                value: 6,
            },
            {
                year: '1997',
                value: 7,
            },
            {
                year: '1998',
                value: 9,
            },
            {
                year: '1999',
                value: 13,
            },
        ];
        const lineConfig = {
            data: lineData,
            xField: 'year',
            yField: 'gdp',
            seriesField: 'name',
            yAxis: {
                label: {
                    formatter: function formatter(v) {
                        return ''.concat((v / 1000000000).toFixed(1), ' B');
                    },
                },
            },
            legend: { position: 'top' },
            smooth: true,
            animation: {
                appear: {
                    animation: 'path-in',
                    duration: 5000,
                },
            },
        };
        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <Text fontSize='23px' fontWeight='bold' color='#333333'>Assets</Text>
                        <img src={CloseBtn} alt="" width='15px' height='15px' onClick={this.goBack} />
                    </HeadContainer>
                    <Text fontSize='18px' fontWeight='500' color='#333333' marginLeft='25px'>Assets distribution</Text>
                    <div>
                        <Pie {...pieConfig} />
                    </div>
                    <Text fontSize='18px' fontWeight='500' color='#333333' marginLeft='25px'>Assets trend</Text>


                </BodyWrapper>
            </AppBody>
        );
    }
}
