import React, {Component} from 'react'
import styled from 'styled-components'
import AppBody, { AppContentWrapper } from '../AppBody'
import { AutoColumn, ColumnCenter } from '../../components/Column'
import Row, { RowBetween, RowFit } from '../../components/Row'
import {GreenText, OrangeText, PinkText, GrayText} from '../../components/TextColor'
import {Text} from "rebass";
import Search from "../../assets/images/search.svg";
import HeaderBackground from "../../assets/images/header_background.svg";
import ActiveValidator from "../../assets/images/active_validator.svg";
import ClvMarketCap from "../../assets/images/clv_market_cap.svg";
import ClvPrice from "../../assets/images/clv_price.svg";
import LatestBlock from "../../assets/images/latest_block.svg";
import Header from "../../components/Header";
import CloverLogo from "../../assets/images/clover_logo.svg";

const ContentTopWrapper = styled.div`
  height: 379px;
  width: 100%;
  background: linear-gradient(180deg, #1E455C 0%, #153040 100%);
  padding-top: 47px;
`

const ContentTopContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  padding-top: 30px;
  width: 1240px;
  height: 384px;
  margin: 0 auto;
  background: url('${HeaderBackground}');
`;

const SearchContainer = styled.div`
  height: 52px;
  margin-top: 42px;
  position: relative;
`;

const SearchInput = styled.input`
  text-indent: 20px;
  width: 754px;
  height: 52px;
  outline: none;
  border: none;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 16px;
  color: #333333;;
  background: #FFFFFF;
  border-radius: 25px;
  padding-right: 60px;
  ::placeholder {
    color: #7E96AE;
  }
  :focus {
    border: 3px solid #4BA870;
  }
`;

const SearchButton = styled.span`
  position: absolute;
  right: 5px;
  top: 2px;
  display: inline-block;
  line-height: 44px;
  color: #ffffff;
  font-size: 16px;
  text-align: center;
  width: 44px;
  height: 44px;
  background: linear-gradient(187deg, #76B96D 0%, #4BA871 100%);
  border-radius: 59px;
  cursor: pointer;
  margin-top: 2px;
  &:hover {
    background: linear-gradient(187deg, #8BED7F 0%, #48C079 100%);
    box-shadow: 0px 1px 8px 1px #6DB480;
  }
`;

const ContentTop = styled.div`
  width: 100%;
  height: 185px;
  background: #ffffff;
  box-shadow: 0px 2px 7px 0px #E9EDEA;
  border-radius: 8px;
  position: relative;
  top: -50px;
  padding: 24px 35px;
`;

const ContentWrapper = styled(AutoColumn)`
  width: 1240px;
  margin: 0 auto;
`;

const ContentTopLeft = styled(AutoColumn)`
  width: 788px;
  border-right: 1px solid #F1F3F8;
`;

const ContentTopRight = styled.div`
    padding-left: 50px;
`;

const ContentBottom = styled(Row)`
  margin-top: -30px;
  margin-bottom: 40px;
`;

const ContentBottomItem = styled.div`
  width: 610px;
  height: 644px;
  background: #FFFFFF;
  box-shadow: 2px 2px 9px 1px #E9EDEA;
  border-radius: 8px;
  padding: 20px;
`;

const ContentListItem = styled(RowBetween)`
  padding: 20px 0;
  border-bottom: 1px solid #EFF1F7;

  :last-child {
    border: none;
  }
`;

const ContentBtn = styled.div`
  width: 100%;
  height: 42px;
  background: #F1F3F5;
  border-radius: 4px;
  color: rgba(0, 24, 49, 0.5);
  text-align: center;
  line-height: 42px;
  margin-top: -5px;
  cursor: pointer;
`;


const SelectNav = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #4BA870;
  padding-left: 48px;
  cursor: pointer;
`;

const NavItem = styled(Text)`
  font-size: 16px;
  color: #7E96AE;
  padding-left: 48px;
  cursor: pointer;
`;

