                <SearchContainer>
                  <SearchInput placeholder='Search by Address / Txn Hash /Token' onChange={this.changeSearchStr} />
                  <SearchButton onClick={this.searchDapps}><span className="fo-search"></span><img width={15} height={15} src={Search} alt=""/></SearchButton>
                </SearchContainer>
              </ContentTopContainer>
            </ContentTopWrapper>
            <ContentWrapper gap={'0'}>
