  tag,
  nativeType,
  block,
  url,
  replace,
  click,
  touchstart,
  icon,
  hairline,
  size = 'normal'
}: Props) {
  const CustomTag = tag || 'button';
  const props = {
    className: classnames(baseClass, [
      { type },
      { plain: plain || hairline },
      { disabled },
      { loading },
      { round },
      { square },
      { block },
      { hairline },
      { [size]: size },
      { onlyIcon: !text && !children }
    ]),
    style: {}
  };

  if (nativeType) Object.assign(props, { nativeType });

  if (loadingSize)
    Object.assign(props, { style: { ...props.style, height: loadingSize } });

  if (fontColor)
    Object.assign(props, { style: { ...props.style, color: fontColor } });

  if (color) {
    if (color.indexOf('linear-gradient') === -1) {
      Object.assign(props, {
        style: {
          ...props.style,
          color: fontColor || getContrastTextColor(color),
          backgroundColor: colorType(color),
          borderColor: colorType(color)
        }
      });
    } else {
      Object.assign(props, {
        style: {
          ...props.style,
          color: fontColor || getContrastTextColor(color),
          background: color
        }
      });
    }
  }

  if (disabled)
    Object.assign(props, {
      disabled
    });

  if (url && tag === 'a') {
    Object.assign(props, {
      href: url
    });
    if (replace) {
      Object.assign(props, {
        target: '_self'
      });
    } else {
      Object.assign(props, {
        target: '_blank'
      });
    }
  }

  if (click) {
    Object.assign(props, {
      onClick: click
    });
  }

  if (click && loading) {
    Object.assign(props, {
      onClick: () => {}
    });
  }

  if (touchstart) {
    Object.assign(props, {
      onTouchStart: touchstart
    });
  }

  if (touchstart && loading) {
    Object.assign(props, {
      onTouchStart: () => {}
    });
  }

