export interface IProps {
  checked?: boolean;
  name?: string;
  activeIcon?: string;
  inactiveIcon?: string;
  checkedColor?: string;
  labelText?: string;
  disabled?: boolean;
  labelDisabled?: boolean;
  isActive?: boolean;
