}

const baseClass = 'vant-checkbox';

// TODO: Round/Square checkbox
// TODO: Checkbox groups
// TODO: Checkbox in Cells (Need to get Will to finish cell component)

const Checkbox = ({
  checked = false,
  changed,
  clicked,
  name,
  activeIcon = 'checked',
  checkedColor = '#1989fa',
  labelText,
  inactiveIcon = 'passed',
  disabled,
  labelDisabled,
  isActive
}: IProps) => {
  const [isChecked, handleCheck] = useState(checked);

  const handleClick = (e) => {
    return clicked && clicked(e);
  };

  useEffect(() => {
    return changed && changed(isChecked);
  }, [isChecked]);

  useEffect(() => {
    isActive ? handleCheck(true) : handleCheck(false);
  }, [isActive]);

  const handleContainerClick = (e) => {
    e.preventDefault();
    if (!disabled && !labelDisabled) {
      handleCheck(!isChecked);
      handleClick(e);
    }
  };

  const handleIconClick = (e) => {
    e.preventDefault();
    if (!disabled) {
      handleCheck(!isChecked);
      handleClick(e);
    }
  };

  const iconName = isChecked ? activeIcon : inactiveIcon;
  const iconColor = disabled ? '#c8c9cc' : checkedColor;

  return (
    <div
      className={classnames(baseClass, [
        {
          disabled
        }
      ])}
      onClick={handleContainerClick}
    >
      <div className={`${baseClass}__icon-container`} onClick={handleIconClick}>
        <Icon color={iconColor} name={iconName} size='20px' />
      </div>
      <label htmlFor={name}>{labelText}</label>
    </div>
  );
};

