import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row from "../../components/Row";
import BackIcon from '../../assets/images/back_icon.svg'
import {Text} from "rebass";
import ImgDappExchange from "../../assets/images/polkadot_exchange.svg";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import SwipeableViews from "react-swipeable-views";
import withStyles from "@material-ui/core/styles/withStyles";
import cs from 'classnames';

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(Row)`
  padding: 0 20px;
  margin-top: 50px;
  height: 44px;
  background-color: #ffffff;
`;

const MyDApps = styled(Text)`
  font-size: 20px;
  font-weight: bold;
  color: #333333;
  text-align: center;
  width: 100%;
`;

const GrayBackground = styled.div`
  width: 100%;
  overflow: hidden;
  background-color: #F7F7F7;
  height: calc(100vh - 144px);
`;

const DappListWrapper = styled.div`
  background-color: #ffffff;
  margin: 10px 20px;
  border-radius: 10px;
  max-height: 88%;
  overflow-x: hidden;
  overflow-y: auto;
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  padding: 15px;
  height: 80px;
`;

const DappContentWrapper = styled(Column)`
  max-width: 80%;
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #666666;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;


export default class MoreDAppsComponent extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            tabList: [
                {tabName:"Recent",id:1},
                {tabName:"Favorites",id:2},
            ],
            currentIndex: 0,
            navTop: false,
            tabs: null,
            offsetTop: 0
        }

    }

    componentDidMount(){
        const { tabs, offsetTop } = this.state;
        if(tabs){
            // offsetTop = tabs.offsetTop;
            window.addEventListener('scroll',this.handleScroll);
        }
    }


    handleScroll(){
        const { tabs, offsetTop } = this.state;
        let sTop = document.documentElement.scrollTop || window.pageYOffset || document.body.scrollTop;

        if(sTop >= offsetTop){
            this.setState({
                navTop: true
            })
        }

        if(sTop < offsetTop){
            this.setState({
                navTop:false
            })
        }
    }

    goBack = () => {
        this.props.history.goBack();
    }

    goDappsDetail = (url: any) => {
        this.props.history.push({pathname: '/dappsdetail', state: {url: url}});
    }

    handleChange = (event: any, value: any) => {
        this.setState({
            currentIndex: value,
        });
    };

    handleChangeIndex = (index: any) => {
        this.setState({
            currentIndex: index,
        });
    };

    render() {
        const { tabList, currentIndex } = this.state;
        const dapplist = [
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            },
            {
                dappName: 'Reef Finance',
                describe: 'Reef the crosschain Defi operating system',
                url: 'https://swap.clover.finance/#/'
            }
        ];
        const slideStyles = {
            tabs: {
                padding: '0 20px',
            },
            slide: {
                padding: '10px 0',
                minHeight: 'fit-content',
                color: '#fff',
                overflow: 'hidden'
            }
        };
        const AntTab = withStyles((theme) => ({
            root: {
                textTransform: 'none',
                fontSize: '15px',
                '&$selected': {
                    color: '#FF7D30',
                    fontWeight: theme.typography.fontWeightMedium,
                },
            },
            selected: {},
        }))(Tab);
        return (
            <AppBody>
                <BodyWrapper gap={'0px'}>
                    <div ref="tab" className={cs({'fixed':this.state.navTop})}>wedfrgh</div>
                    <HeadContainer>
                        <img onClick={this.goBack} src={BackIcon} alt="" width='9px' height='16px' />
                        <MyDApps>My DApps</MyDApps>
                    </HeadContainer>

                    <GrayBackground>
                        <Tabs value={currentIndex} onChange={this.handleChange} style={slideStyles.tabs}
                              TabIndicatorProps={{
                                  style: {
                                      height: '4px',
                                      width: '17px',
                                      backgroundColor: 'rgba(246, 136, 59, 1)',
                                      borderRadius: '2px',
                                      marginLeft: '30px'
                                  }
                              }}
                        >
                            {tabList.map((res: any) => (
                                <AntTab label={res.tabName}/>
                            ))}
                        </Tabs>
                        <DappListWrapper>
                            <SwipeableViews index={currentIndex} onChangeIndex={this.handleChangeIndex}>
                                {tabList.map(() => (
                                    <div style={slideStyles.slide}>
                                        {dapplist.map((dapp: any) => (
                                            <DappContainer onClick={() => { this.goDappsDetail(dapp.url); }}>
                                                <img height="50" width="50" src={ImgDappExchange} alt='Polkadot Exchange' />
                                                <DappContentWrapper>
                                                    <Column>
                                                        <DappName>{dapp.dappName}</DappName>
                                                        <DappDescription>{dapp.describe}</DappDescription>
                                                    </Column>
                                                </DappContentWrapper>
                                            </DappContainer>
                                        ))}
                                    </div>
                                ))}
                            </SwipeableViews>
                        </DappListWrapper>
                    </GrayBackground>
                </BodyWrapper>
            </AppBody>
        );
    }
}
