import React, { Component } from 'react';
import styled from 'styled-components'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, {RowStart, RowCenter} from "../../components/Row";
import BackIcon from '../../assets/images/back_icon.svg'
import {Text} from "rebass";

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(Row)`
  padding: 0 20px;
  margin-top: 50px;
  background-color: #ffffff;
`;

const MyDApps = styled(Text)`
  font-size: 20px;
  font-weight: bold;
  color: #333333;
  text-align: center;
  width: 100%;
`;

const IframeContainer = styled.div`
  width: 100%;
  height: calc(100vh - 100px);
`;

export default class DAppsDetailComponent extends Component<any, any> {
    componentDidMount() {
        document.documentElement.style.overflow = "hidden";
        document.documentElement.style.height = "100%";
        document.body.style.overflow = "hidden";
        document.body.style.height = "100%";
        // document.getElementById("qqq").style.height = "100%";
        // document.getElementById("root-div").style.height = "100%";
        // document.getElementsByClassName("App")[0].style.height = "100%";
    }
    componentWillUnmount() {
        document.documentElement.style.overflow = "auto";
        document.documentElement.style.height = "auto";
        document.body.style.overflow = "auto";
        document.body.style.height = "auto";
        // document.getElementById("qqq").style.height = "auto";
        // document.getElementById("root-div").style.height = "auto";
        // document.getElementsByClassName("App")[0].style.height = "auto";
    }

        goBack = () => {
        this.props.history.goBack();
    }

    render() {
        return (
            <AppBody>
                <BodyWrapper gap={'md'}>
                    <HeadContainer>
                        <img onClick={this.goBack} src={BackIcon} alt="" width='9px' height='16px' />
                        <MyDApps>DApps Detail</MyDApps>
                    </HeadContainer>
                    <IframeContainer>

                    </IframeContainer>
                    <div style={{height: '100%', width: '100%'}}>
                        <iframe
                            id="iframeFaq"
                            title="study"
                            src="https://www.csdn.net/"
                            width="100%"
                            height="100%"
                        ></iframe>
                    </div>
                </BodyWrapper>
            </AppBody>
        );
    }
}
