import React, {Component} from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn, ColumnCenter } from '../../components/Column'
import Row, {RowBetween, RowEnd} from '../../components/Row'
import CloseBtn from '../../assets/images/closeBtn.svg'
import { ResponsivePie } from '@nivo/pie';
import { ResponsiveLine } from '@nivo/line';
import { ResponsiveBar } from "@nivo/bar";
import { getOrdinalColorScale } from '@nivo/colors';
import { LineChart, Line, PieChart, Pie, Cell, Tooltip, Sector, Legend } from 'recharts';

const BodyWrapper = styled(AutoColumn)`
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
  height: 44px;
`;

const PieChartWrapper = styled.div`
  //margin: 20px 0;
  height: 250px;
  display: flex;
  justify-content: center;
`;

const LineChartWrapper = styled.div`
  margin: 0 30px;
  height: 250px;
  background: linear-gradient(180deg,#FFFFFF 0%, #FFF6FA 50%, #FFFFFF 100%);
  border-radius: 15px;
`;

const BarChartWrapper = styled.div`
  position: relative;
  margin: 20px 30px;
