import React, {Component} from 'react'
import styled from 'styled-components'
import AppBody from '../AppBody'
import { AutoColumn } from '../../components/Column'
import Row, { RowBetween } from '../../components/Row'
import { IconSM, IconMD, IconGray } from '../../components/IconSpan'
import {GreenText, GreenTextInline, OrangeText, PinkText, GrayText} from '../../components/TextColor'
import {Text} from "rebass";
import Forward from "../../assets/images/forward.svg";
import HistoryIcon from "../../assets/images/history_icon.svg";
import FilterIcon from "../../assets/images/filter_icon.svg";
import FilterWhite from "../../assets/images/filter_white.svg";
import EyeIcon from "../../assets/images/eye_icon.svg";
import ArrowDown from "../../assets/images/arrow_down.svg";
import ArrowUp from "../../assets/images/arrow_up.svg";
import ArrowRight from "../../assets/images/arrow_right.svg";
import TokenIcon from "../../assets/images/token_icon.svg";
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import withStyles from '@material-ui/core/styles/withStyles';
import InputBase from '@material-ui/core/InputBase';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import Header from '../../components/Header'
import SearchComponent from '../../components/Search'
import ContentHeader from '../../components/ContentHeader'
import TableBottomComponent from '../../components/TableBottom'
import CloverLogo from "../../assets/images/clover_logo.svg";

const BodyWrapper = styled.div`
  width: 1240px;
  margin: 50px auto 20px;
`;

const ContentTop = styled.div`
  height: 265px;
  background: #FFFFFF;
  box-shadow: 0px 2px 7px 0px #E9EDEA;
  border-radius: 8px;
  padding: 30px;
`;

const ContentTopItem = styled(Row)`
  padding: 10px 20px;
  height: 50px;
  width: 540px;
  border-bottom: 1px solid #E6E8ED;
  &:last-child {
    border-bottom: none;
  }
`;

const ContentWrapper = styled.div`
  padding: 20px;
  background: #FFFFFF;
  border-radius: 8px;
`;

const TableWrapper = styled.table`
  width: 100%;
`;

const TableHeader = styled.tr`
  height: 50px;
  background: #F5F5F5;
`;

const ThWrapper = styled.th`
  color: rgba(0, 24, 49, 0.5);
  font-size: 16px;
  font-weight: bold;
`;

const ThFilterWrapper = styled.th`
  color: rgba(0, 24, 49, 0.5);
  font-size: 16px;
  font-weight: bold;
  position: relative;
  cursor: pointer;
`;

const TableTrWrapper = styled.tr`
  line-height: 55px;
  vertical-align: top;
  border-bottom: 1px solid #E8EAEE;
  &:last-child {
    border-bottom: none;
  }
`;

const TokenSelect = styled(Select)`
  width: 323px;
  height: 32px;
  padding: 4px 8px;
  font-size: 14px;
  border-radius: 2px;
  border: 1px solid #E6E8ED;
`;

const FilterModalWrapper = styled.div`
  position: absolute;
  top: 40px;
  width: 218px;
  height: 103px;
  background: #FFFFFF;
  box-shadow: 0px 2px 7px 0px #E9EDEA;
  border-radius: 4px;
  padding: 15px 10px;
`;

const FilterSearchInput = styled.input`
  text-overflow: ellipsis;
  font-size: 14px;
  background: transparent;
  color: #333333;
  width: 100%;
  height: 31px;
  border-radius: 2px;
  border: 1px solid #E6E8ED;
  text-indent: 10px;
  margin-bottom: 10px;

  ::placeholder {
    color: rgba(0, 24, 49, 0.5);
  }
`;

const FilterButton = styled.div`
  width: 94px;
  height: 30px;
  background: linear-gradient(180deg, #76B96D 0%, #4BA871 100%);
  border-radius: 4px;
  cursor: pointer;
  color: #ffffff;
  line-height: 30px;
  text-align: center;
  &:hover {
    background: #368949;
  }
`;

const ClearButton = styled.div`
  width: 94px;
  height: 30px;
  background: #FFAC3D;
  color: #ffffff;
  border-radius: 4px;
  cursor: pointer;
  line-height: 30px;
  text-align: center;
  &:hover {
    background-color: #CE8729;
  }
`;

const SelectItemBtn = styled.span`
  display: inline-block;
  width: 52px;
  height: 27px;
  background: #FFAC3D;
  border-radius: 4px;
  color: #ffffff;
  font-size: 16px;
  text-align: center;
  line-height: 27px;
  margin-left: 5px;
`;

const OwnershipBtn = styled.div`
  width: 232px;
  height: 35px;
  line-height: 35px;
  text-align: center;
  color: #ffffff;
  font-size: 16px;
  background: linear-gradient(187deg, #76B96D 0%, #4BA871 100%);
  border-radius: 6px;
  cursor: pointer;
`;

const HashTime = styled.div`
  width: 93px;
  height: 20px;
  line-height: 20px;
  background: #F5F5F5;
  border-radius: 4px;
  font-size: 14px;
  color: rgba(0, 24, 49, 0.5);
  text-align: center;
`;

const HistoryBtn = styled(Row)`
  cursor: pointer;
  color: #7F8B98;
`;

const CodeWrapper = styled.div`
  width: 100%;
  overflow-y: scroll;
  vertical-align: top;
  border-color: #E6E8ED;
  background-color: #F9F9F9;
  border-radius: 5px;
  margin-top: 20px;
  padding: 20px;
  font-size: 14px;
`;

export default class Contract extends Component<any, any> {
    constructor(props: any, context: any) {
        super(props, context);
        this.state = {
            tabList: [
                {tabName: "Transactions", id: 1},
                {tabName: "Internal Txns", id: 2},
                {tabName: "Contract", id: 3},
                {tabName: "Events", id: 4}
            ],
            currencyToken: '1',
            currentTabIndex: 2,
            openFromFilter: false,
            openToFilter: false,
            fromSearchStr: '',
            toSearchStr: '',
            activeIndex: null,
            showAddresses: false
        }
    }

    handleChange = (event: any, value: any) => {
        this.setState({
            currentTabIndex: value
        });
    };

    handleChangeToken = (event: any) => {
        this.setState({
            currencyToken: event.target.value
        });
    };

    handleFromClick = (event: any) => {
        event.stopPropagation();
        event.preventDefault();
        const { openFromFilter } = this.state;
        this.setState({
            openFromFilter: !openFromFilter
        })
    };

