import React, { Component } from 'react';
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn } from '../../components/Column'
import Row, { RowBetween, RowFit, RowCenter } from '../../components/Row'
import ImgDappExchange from '../../assets/images/polkadot_exchange.svg'
import Application from '../../assets/images/application.svg'
import SearchIcon from '../../assets/images/search_icon.svg'
import SwapIcon from '../../assets/images/swap_icon.svg'
import LendingIcon from '../../assets/images/lending_icon.svg'
import AggregationIcon from '../../assets/images/aggregation_icon.svg'
import AuctionIcon from '../../assets/images/auction_icon.svg'
import InsuranceIcon from '../../assets/images/insurance_icon.svg'
// import SwipeViews from 'react-swipe-views';
// import SwipeableViews from 'react-swipeable-views';
import Tabs, { TabPane } from 'rc-tabs';
import 'rc-tabs/assets/index.css';


const BodyWrapper = styled(AutoColumn)`
  height: calc(100vh - 50px);
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
`;

const DAppIconListTitle = styled(RowBetween)`
  margin-top: 10px;
  padding: 0 20px;
`;

const DAppIconListWrapper = styled(RowBetween)`
  margin-top: 15px;
  padding: 0 20px;
`;

const DappIconWrapper = styled.div`
  text-align: center;
`;

const SwapContainer = styled.div`
  margin: 0 20px;
  padding: 20px;
  background: #F6883B;
  border-radius: 15px;
  opacity: 0.08;
`;

const SecondTitle = styled(Text)`
  font-size: 18px;
  color: #333333;
  font-weight: 600;
`;

const GrayBackground = styled.div`
  background: linear-gradient(180deg, #FFFFFF 0%, #F3f3f3 100%);
`;

const SwapButton = styled(RowCenter)`
  width: 75px;
  height: 30px;
  background: rgba(251, 130, 42, 0.1);
  border-radius: 30px;
`;

const MenuWrapper = styled.img`
  margin-left: 20px;
`;

const Search = styled.img`
  margin-left: 20px;
  width: 27px;
  height: 27px;
`;

const NavBar = styled(RowBetween)`
  margin-top: 15px;
  padding: 0 20px;
  align-items: flex-start;
`;

const NavLink = styled(Text)`
  font-size: 16px;
  font-weight: 500;
  color: #41485D;
  text-decoration: none;
`;

const NavLinkActive = styled(NavLink)`
  color: #FB822A;
  padding-bottom: 3px;
  border-bottom: 2px solid #FB822A;
`;

const DappListWrapper = styled.div`
  background-color: #ffffff;
  margin: 0 20px;
  border-radius: 10px;
`;

const DappContainer = styled(Row)`
  align-items: stretch;
  margin-top: 10px;
  margin-bottom: 10px;
  padding: 15px;
`;

const DappContentWrapper = styled(Column)`
  margin-left: 10px;
  justify-content: space-between;
`;

const DappName = styled(Text)`
  font-size: 16px;
  font-weight: bold;
  color: #000000;
`;

const DappDescription = styled(Text)`
  font-size: 12px;
  font-weight: normal;
  color: #666666;
`;

export default class DappM extends Component<any, any> {
  constructor(props: any, context: any) {
    super(props, context);
    this.state = {
      tabList: [
        {tabName:"Defi",id:1},
        {tabName:"Bright",id:2},
        {tabName:"Grant",id:3},
        {tabName:"Exange",id:4},
        {tabName:"NFT",id:5}
      ],
      currentIndex: 1,
    }
  }

  tabChoiced = (id: any) => {
    this.setState({
      currentIndex:id
    });
  }

  searchDapps = () => {
    this.props.history.push('/search');
  }

  moreDapps = () => {
    this.props.history.push('/moredapps');
  }

  goDappsDetail = (dapp: any) => {
    console.log(dapp);
    this.props.history.push('/dappsdetail');
  }

  render() {
    const { tabList, currentIndex } = this.state;
    const recommendDapps = [
      {
        icon: SwapIcon,
        appName: 'Swap'
      },
      {
        icon: LendingIcon,
        appName: 'Lending'
      },
      {
        icon: AggregationIcon,
        appName: 'Aggregation'
      },
      {
        icon: AuctionIcon,
        appName: 'Auction'
      },
      {
        icon: InsuranceIcon,
        appName: 'Insurance'
      },
    ];
    const dapplist = [
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      },
      {
        dappName: 'Reef Finance',
        describe: 'Reef the crosschain Defi operating system'
      }
    ]

    const styles = {
      slide: {
        padding: 15,
        minHeight: 100,
        color: '#fff',
      },
      slide1: {
        backgroundColor: '#FEA900',
      },
      slide2: {
        backgroundColor: '#B3DC4A',
      },
      slide3: {
        backgroundColor: '#6AC0FF',
      },
    };

    return (
        <AppBody>
          <BodyWrapper gap={'md'}>
            <HeadContainer>
              <img width="148" height="18" src={Application} alt=""/>
              <Search src={SearchIcon} alt='search' onClick={this.searchDapps} />
              {/*<RowFit>*/}
                {/*<SwapButton>*/}
                {/*  <Text fontSize='15px' fontWeight='bold' color='#FB822A'>SWAP</Text>*/}
                {/*</SwapButton>*/}
                {/*<MenuWrapper src={ImgMenu} alt='menu' />*/}
              {/*</RowFit>*/}
            </HeadContainer>
            {/*<SwipeableViews enableMouseEvents>*/}
            {/*  <div style={Object.assign({}, styles.slide, styles.slide1)}>slide n°1</div>*/}
            {/*  <div style={Object.assign({}, styles.slide, styles.slide2)}>slide n°2</div>*/}
            {/*  <div style={Object.assign({}, styles.slide, styles.slide3)}>slide n°3</div>*/}
            {/*</SwipeableViews>*/}
            <div className="App">
              <Tabs defaultActiveKey="2">
                <TabPane tab="tab 1" key="1">
                  Lorem ipsum dolor sit amet, id duis noster delectus vis, at agam purto atqui usu. Eu eum sale choro. Ne quot equidem conclusionemque sed. Ludus referrentur ex quo. Fuisset disputando consequuntur ad nec, qui praesent postulant efficiendi ut.
                </TabPane>
                <TabPane tab="tab 2" key="2">
                  Est vidit deseruisse in, mei ullum blandit maluisset ad. Ut sea iriure dolores. Vel tollit eirmod dolores ea, errem periculis usu ut. Eum alii soleat dissentiet ne. Ridens accumsan cum an, ei eum solum pertinacia democritum.
                </TabPane>
                <TabPane tab="tab 3" key="3">
                  Facete fierent ea mel, id nec delectus invenire elaboraret. Vel illum mollis eu, sit in latine theophrastus, mei nulla suscipit comprehensam ad.
                </TabPane>
              </Tabs>
            </div>
            <div>
              <DAppIconListTitle>
                <SecondTitle>My DApps</SecondTitle>
                <Text color='#333333' fontSize='14px' onClick={this.moreDapps}>more</Text>
              </DAppIconListTitle>
              <DAppIconListWrapper>
                {recommendDapps.map((dapp) => (
                    <DappIconWrapper>
                      <img width='50' height='50' src={dapp.icon} alt="" />
                      <Text fontSize='12px' color='#666666' marginTop='5px'>{dapp.appName}</Text>
                    </DappIconWrapper>
                ))}
              </DAppIconListWrapper>
            </div>
            <div>
