import React, { ReactElement, useContext } from 'react'
import { useTranslation } from 'react-i18next'
import { Moon, Sun } from 'react-feather'
import styled, { ThemeContext } from 'styled-components'
import { useDarkMode, useSetDarkMode } from '../../state/settings/hooks'
import { TYPE } from '../../theme'
import Row, { RowCenter, RowBetween, RowFit } from '../Row'
import { Text } from 'rebass'

import ImgPolkadot from '../../assets/images/polkadot.svg'
import CloverLogo from '../../assets/images/clover_logo.svg';

const ContentWrapper = styled(RowBetween)`
  background-color: #ffffff;
`

const ContentContainer = styled(RowBetween)`
  height: 54px;
  width: 1240px;
  margin: 0 auto;
`

const MenuItemWrapper = styled(RowCenter)`
  margin-right: 20px;
  padding: 10px 20px;

  border: ${({ theme }) => `2px solid ${theme.border1}` };
  border-radius: 32px;
  box-sizing: border-box;
`

const ChainLogo = styled.img`
  margin-right: 10px;

  width: 43px;
  height: 43px;
`

export default function Header(): ReactElement {
  const { t } = useTranslation()

  const theme = useContext(ThemeContext)

  const darkMode = useDarkMode()
  const setDarkMode = useSetDarkMode()

  return (
      <ContentWrapper>
        <ContentContainer>
          <RowFit>
            <img src={CloverLogo} width='151' height='38' alt=""/>
          </RowFit>

          <RowFit>
            <Row>
              <Text fontSize={16} fontWeight='bold' color='#27C96E' marginRight={48}>Home</Text>
              <Text fontSize={16} color='#7E96AE' marginRight={48}>Blockchain</Text>
              <Text fontSize={16} color='#7E96AE' marginRight={48}>Tokens</Text>
              <Text fontSize={16} color='#7E96AE'>Resources</Text>
            </Row>
            {/*<MenuItemWrapper>*/}
            {/*  <TYPE.menuItem>{t('transactionHistory')}</TYPE.menuItem>*/}
            {/*</MenuItemWrapper>*/}
            {/*<MenuItemWrapper style={{padding: '1px 20px 1px 1px'}}>*/}
            {/*  <ChainLogo src={ImgPolkadot} />*/}
            {/*  <TYPE.menuItem>{t('polkadotChain')}</TYPE.menuItem>*/}
            {/*</MenuItemWrapper>*/}

            {/*{darkMode ? <Sun color={theme.white} size={24} style={{cursor: 'hand'}} onClick={() => setDarkMode(false)} />*/}
            {/*    : <Moon color={theme.black} size={24} style={{cursor: 'hand'}} onClick={() => setDarkMode(true)} />*/}
            {/*}*/}
          </RowFit>
        </ContentContainer>
      </ContentWrapper>
  )
}
