import React from 'react'
import styled from 'styled-components'
import { Text } from 'rebass'
import AppBody from '../AppBody'
import Column, { AutoColumn, ColumnCenter } from '../../components/Column'
import Row, {RowBetween, RowEnd} from '../../components/Row'
import Profit from '../../assets/images/profit.svg'
import ProfitLoss from "../../assets/images/profitLoss.svg";
import Interest from "../../assets/images/interest.svg";
import SwapIcon from '../../assets/images/swap_icon.svg'

const BodyWrapper = styled(AutoColumn)`
  background: linear-gradient(180deg, #FFFFFF 0%, #FFFFFF 34%, #F7F7F7 100%);
`;

const HeadContainer = styled(RowBetween)`
  margin-top: 50px;
  padding: 0 20px;
  height: 44px;
`;

const AssetsOverviewContainer = styled(Column)`
  align-items: stretch;
  padding: 20px 20px 30px 20px;
  background: linear-gradient(223deg, #FA6094 0%, #FF6F1A 29%, #FF4A95 100%);
  border-radius: 17px;
  margin: 0 20px;
`;

const DetailsBtn = styled.div`
  width: 67px;
  height: 32px;
  background: rgba(51, 51, 51, 0.15);
  border-radius: 16px;
  color: #ffffff;
  text-align: center;
  line-height: 32px;
`;

const NetWorthContainer = styled(ColumnCenter)`
  margin: 10px 5px;
`;

const TotalContainer = styled(Row)`
  margin: 0 5px;
`;

const TotalChildContainer = styled(ColumnCenter)`
    margin-right: 25px;
`;

const TransferDetail = styled(RowBetween)`
  padding: 15px;
  margin-top: 35px;
  background: linear-gradient(180deg, rgba(255, 255, 255, 0.08) 0%, rgba(252, 231, 231, 0.08) 100%);
  border-radius: 15px;
  border: 1px solid rgba(151, 151, 151, 0.08);
`;

const TransferItem = styled(ColumnCenter)`
  align-items: center;
`;

const AssetsContainer = styled(RowBetween)`
  padding: 0 25px;
  height: 47px;
`;

const AssetsDetailWrapper = styled.div`
  margin: 0 20px;
  background: #ffffff;
  border-radius: 5px;
  padding: 12px 15px;
`;

const AssetsName = styled(Row)`

`;

const AssetsDetail = styled(RowBetween)`

`;

const WalletAssetsTitleContainer = styled(RowBetween)`
  margin: 50px 20px 0px 20px;
  background: #ffffff;
`;

const WalletAssetsContainer = styled.div`
  display: grid;
  grid-template-columns: auto auto auto auto;
  grid-row-gap: 15px;
  grid-column-gap: 10px;

  margin: 0px 20px;
`;

const WalletAssetRowSeparator = styled.div`
  height: 1px;
  background: rgba(0, 0, 0, 0.05);
  grid-column: 1 / 5;
`;

const WalletAssetColumnHeader = styled(Text)`
  font-size: 14px;
  font-weight: normal;
  color: rgba(65, 72, 93, 0.5);
`;

const WalletAssetRowHeader = styled(Text)`
  font-size: 14px;
  font-weight: bold;
  color: #000000;
`;

const WalletAssetRowValue = styled(Text)`
  font-size: 14px;
  font-weight: normal;
  color: #000000;
`;

export default function Assets(): React.ReactElement {
  return (
    <AppBody>
      <BodyWrapper gap={'md'}>
        <HeadContainer>
          <Text fontSize="23px" fontWeight="bold">Assets</Text>
        </HeadContainer>
        <AssetsOverviewContainer>
          <RowEnd>
            <DetailsBtn>details</DetailsBtn>
          </RowEnd>
          <NetWorthContainer>
            <Text fontSize='14px' fontWeight='normal' color='#FFFFFF'>net assets</Text>
            <Text fontSize='40px' fontWeight='bold' color='#FFFFFF'>$23405.00</Text>
          </NetWorthContainer>
          <TotalContainer>
            <TotalChildContainer>
              <Text fontSize='14px' fontWeight='normal' color='#FFFFFF'>Total assets</Text>
              <Text fontSize='17px' fontWeight='bold' color='#FFFFFF'>$234995.300</Text>
            </TotalChildContainer>
            <TotalChildContainer>
              <Text fontSize='14px' fontWeight='normal' color='#FFFFFF'>Total liabilities</Text>
              <Text fontSize='17px' fontWeight='bold' color='#FFFFFF'>$2345.300</Text>
            </TotalChildContainer>
          </TotalContainer>
          <TransferDetail>
            <TransferItem>
              <img src={ProfitLoss} alt="" width='25' height='25' />
              <Text fontSize='12px' fontWeight='normal' color='#FFFFFF' margin='5px 0'>Earnings today</Text>
              <Text fontSize='14px' fontWeight='bold' color='#FFFFFF'>$25.00</Text>
            </TransferItem>
            <TransferItem>
              <img src={Profit} alt="" width='25' height='25' />
              <Text fontSize='12px' fontWeight='normal' color='#FFFFFF' margin='5px 0'>Income today</Text>
              <Text fontSize='14px' fontWeight='bold' color='#FFFFFF'>$25.00</Text>
            </TransferItem>
            <TransferItem>
              <img src={Interest} alt="" width='25' height='25' />
              <Text fontSize='12px' fontWeight='normal' color='#FFFFFF' margin='5px 0'>Interest today</Text>
              <Text fontSize='14px' fontWeight='bold' color='#FFFFFF'>$25.00</Text>
            </TransferItem>
          </TransferDetail>
        </AssetsOverviewContainer>

        {/*<TotalAssetsAndDebtsContainer>*/}
        {/*  <ColumnCenter>*/}
        {/*    <Text fontSize='14px' fontWeight='normal' color='rgba(65, 72, 93, 0.5)'>Total Assets</Text>*/}
        {/*    <Text fontSize='14px' fontWeight='bold' color='#41485D'>$ 135,451,435</Text>*/}
        {/*    <Text fontSize='14px' fontWeight='normal' color='rgba(65, 72, 93, 0.5)'>Est Daily Intr Earned</Text>*/}
