"use strict";

var _create = require("@polkadot/types/create");

var _MagicNumber = require("./MagicNumber");

// Copyright 2017-2020 @polkadot/metadata authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('MagicNumber', () => {
  const registry = new _create.TypeRegistry();
  it('succeeds when the magic number matches', () => {
    expect(() => new _MagicNumber.MagicNumber(registry, _MagicNumber.MAGIC_NUMBER)).not.toThrow();
  });
  it('fails when the magic number mismatches', () => {
    expect(() => new _MagicNumber.MagicNumber(registry, 0x12345)).toThrow(/MagicNumber/);
  });
});