describe('state', () => {
  it('requires an ws:// prefixed endpoint', () => {
    expect(() => new _.WsProvider('http://', 0)).toThrow(/with 'ws/);
  });
  it('allows wss:// endpoints', () => {
    expect(() => new _.WsProvider('wss://', 0)).not.toThrow();
  });
});