"use strict";

var _create = require("../create");

var _primitive = require("../primitive");

var _ = require(".");

// Copyright 2017-2020 @polkadot/types authors & contributors
// SPDX-License-Identifier: Apache-2.0
const registry = new _create.TypeRegistry();
const mockU32Set = new Set();
mockU32Set.add(new _primitive.U32(registry, 2));
mockU32Set.add(new _primitive.U32(registry, 24));
mockU32Set.add(new _primitive.U32(registry, 30));
mockU32Set.add(new _primitive.U32(registry, 80));
const mockU32SetString = '[2,24,30,80]';
const mockU32SetObject = [2, 24, 30, 80];
const mockU32SetHexString = '0x1002000000180000001e00000050000000';
const mockU32SetUint8Array = Uint8Array.from([16, 2, 0, 0, 0, 24, 0, 0, 0, 30, 0, 0, 0, 80, 0, 0, 0]);
describe('BTreeSet', () => {
  describe('decoding', () => {
    const testDecode = (type, input, output) => it(`can decode from ${type}`, () => {
      const s = new _.BTreeSet(registry, _primitive.U32, input);
      expect(s.toString()).toBe(output);
    });

    testDecode('Set', mockU32Set, mockU32SetString);
    testDecode('hex', mockU32SetHexString, mockU32SetString);
    testDecode('Uint8Array', mockU32SetUint8Array, mockU32SetString);
    testDecode('Set', mockU32Set, mockU32SetString);
    testDecode('hex', mockU32SetHexString, mockU32SetString);
    testDecode('Uint8Array', mockU32SetUint8Array, mockU32SetString);
  });
  describe('encoding multiple values', () => {
    const testEncode = (to, expected) => it(`can encode ${to}`, () => {
      const s = new _.BTreeSet(registry, _primitive.U32, mockU32Set);
      expect(s[to]()).toEqual(expected);
    });

    testEncode('toHex', mockU32SetHexString);
    testEncode('toJSON', mockU32SetObject);
    testEncode('toU8a', mockU32SetUint8Array);
    testEncode('toString', mockU32SetString);
  });
  it('decodes null', () => {
    expect(new (_.BTreeSet.with(_primitive.U32))(registry, null).toString()).toEqual('[]');
  });
  it('decodes reusing instantiated inputs', () => {
    const foo = new _primitive.Text(registry, 'bar');
    expect(new _.BTreeSet(registry, _primitive.Text, new Set([foo])).eq(new Set([foo]))).toBe(true);
  });
  it('decodes within more complicated types', () => {
    const s = new _.Struct(registry, {
      placeholder: _primitive.U32,
      value: _.BTreeSet.with(_primitive.U32)
    });
    s.set('value', new _.BTreeSet(registry, _primitive.U32, mockU32Set));
    expect(s.toString()).toBe('{"placeholder":0,"value":[2,24,30,80]}');
  });
  it('throws when it cannot decode', () => {
    expect(() => new (_.BTreeSet.with(_primitive.U32))(registry, 'ABC')).toThrowError(/BTreeSet: cannot decode type/);
  });
  it('correctly encodes length', () => {
    expect(new (_.BTreeSet.with(_primitive.U32))(registry, mockU32Set).encodedLength).toEqual(17);
  });
  it('generates sane toRawTypes', () => {
    expect(new (_.BTreeSet.with(_primitive.U32))(registry).toRawType()).toBe('BTreeSet<u32>');
    expect(new (_.BTreeSet.with(_primitive.Text))(registry).toRawType()).toBe('BTreeSet<Text>');
    expect(new (_.BTreeSet.with(_.Struct.with({
      a: _primitive.U32,
      b: _primitive.Text
    })))(registry).toRawType()).toBe('BTreeSet<{"a":"u32","b":"Text"}>');
  });
});