"use strict";

var _create = require("../create");

var _primitive = require("../primitive");

var _ = require(".");

// Copyright 2017-2020 @polkadot/types authors & contributors
// SPDX-License-Identifier: Apache-2.0
const registry = new _create.TypeRegistry();
describe('HashMap', () => {
  it('generates sane toRawTypes', () => {
    expect(new (_.HashMap.with(_primitive.Text, _primitive.U32))(registry).toRawType()).toBe('HashMap<Text,u32>');
    expect(new (_.HashMap.with(_primitive.Text, _primitive.Text))(registry).toRawType()).toBe('HashMap<Text,Text>');
    expect(new (_.HashMap.with(_primitive.Text, _.Struct.with({
      a: _primitive.U32,
      b: _primitive.Text
    })))(registry).toRawType()).toBe('HashMap<Text,{"a":"u32","b":"Text"}>');
  });
});