"use strict";

var _ConsensusEngineId = require("./ConsensusEngineId");

// Copyright 2017-2020 @polkadot/types authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('ConsensusEngineId', () => {
  it('creates a valid id for aura', () => {
    expect(_ConsensusEngineId.GenericConsensusEngineId.stringToId('FRNK')).toEqual(_ConsensusEngineId.CID_GRPA);
    expect(_ConsensusEngineId.GenericConsensusEngineId.stringToId('aura')).toEqual(_ConsensusEngineId.CID_AURA);
  });
  it('reverses an id to string for babe', () => {
    expect(_ConsensusEngineId.GenericConsensusEngineId.idToString(_ConsensusEngineId.CID_BABE)).toEqual('BABE');
  });
});