"use strict";

var _create = require("../create");

var _ = require(".");

// Copyright 2017-2020 @polkadot/types authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('Bool', () => {
  const registry = new _create.TypeRegistry();
  describe('decode', () => {
    // eslint-disable-next-line @typescript-eslint/ban-types
    const testDecode = (type, input, expected) => it(`can decode from ${type}`, () => {
      expect(new _.Bool(registry, input).toJSON()).toBe(expected);
    });

    testDecode('Bool', new _.Bool(registry, true), true);
    testDecode('Boolean', Boolean(true), true);
    testDecode('boolean', true, true);
    testDecode('number', 1, true);
    testDecode('Uint8Array', Uint8Array.from([1]), true);
  });
  describe('encode', () => {
    const testEncode = (to, expected, value) => it(`can encode ${to}`, () => {
      expect(new _.Bool(registry, value)[to]()).toEqual(expected);
    });

    testEncode('toJSON', true, true);
    testEncode('toHex', '0x01', true);
    testEncode('toString', 'true', true);
    testEncode('toU8a', Uint8Array.from([1]), true);
    testEncode('toU8a', Uint8Array.from([0]), false);
  });
  it('correctly encodes length', () => {
    expect(new _.Bool(registry, true).encodedLength).toEqual(1);
  });
  describe('utils', () => {
    it('compares against a boolean', () => {
      expect(new _.Bool(registry, true).eq(true)).toBe(true);
    });
    it('compares against a Bool', () => {
      expect(new _.Bool(registry, false).eq(new _.Bool(registry, false))).toBe(true);
    });
    it('has isTrue', () => {
      expect(new _.Bool(registry, true).isTrue).toBe(true);
    });
    it('has isFalse', () => {
      expect(new _.Bool(registry, true).isFalse).toBe(false);
    });
    it('has sane isEmpty aligning with the rest', () => {
      expect(new _.Bool(registry).isEmpty).toBe(true);
      expect(new _.Bool(registry, false).isEmpty).toBe(true);
      expect(new _.Bool(registry, true).isEmpty).toBe(false);
    });
  });
});