"use strict";

var _create = require("../create");

var _ = require(".");

// Copyright 2017-2020 @polkadot/types authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('DoNotConstruct', () => {
  const registry = new _create.TypeRegistry();
  it('does not allow construction', () => {
    expect(() => new (_.DoNotConstruct.with())(registry)).toThrow(/Cannot construct unknown type DoNotConstruct/);
  });
  it('does not allow construction (with Name)', () => {
    expect(() => new (_.DoNotConstruct.with('Something'))(registry)).toThrow(/Cannot construct unknown type Something/);
  });
});