"use strict";

var _create = require("../create");

var _ = require(".");

// Copyright 2017-2020 @polkadot/types authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('Null', () => {
  const registry = new _create.TypeRegistry();
  it('compares against null', () => {
    expect(new _.Null(registry).eq(null)).toBe(true);
  });
  it('compares against Null', () => {
    expect(new _.Null(registry).eq(new _.Null(registry))).toBe(true);
  });
  it('compares against other (failed)', () => {
    expect(new _.Null(registry).eq()).toBe(false);
  });
  it('has no hash', () => {
    expect(() => new _.Null(registry).hash).toThrow();
  });
  it('isEmpty', () => {
    expect(new _.Null(registry).isEmpty).toBe(true);
  });
  it('has an empty hex', () => {
    expect(new _.Null(registry).toHex()).toEqual('0x');
  });
  it('has a Null type', () => {
    expect(new _.Null(registry).toRawType()).toEqual('Null');
  });
});