"use strict";

var _create = require("../create");

var _Raw = require("../codec/Raw");

var _ = require(".");

// Copyright 2017-2020 @polkadot/types authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('Text', () => {
  const registry = new _create.TypeRegistry();
  describe('decode', () => {
    const testDecode = (type, input, expected, toFn = 'toString') => it(`can decode from ${type}`, () => {
      expect(new _.Text(registry, input)[toFn]()).toBe(expected);
    });

    testDecode('string', 'foo', 'foo');
    testDecode('Text', new _.Text(registry, 'foo'), 'foo');
    testDecode('Uint8Array', Uint8Array.from([12, 102, 111, 111]), 'foo');
    testDecode('Raw', new _Raw.Raw(registry, Uint8Array.from([102, 111, 111])), 'foo'); // no length

    testDecode('object with `toString()`', {
      toString() {
        return 'foo';
      }

    }, 'foo');
    testDecode('hex input value', new _.Text(registry, '0x12345678'), '0x12345678', 'toHex');
    testDecode('null', null, '');
  });
  describe('encode', () => {
    const testEncode = (to, expected) => it(`can encode ${to}`, () => {
      expect(new _.Text(registry, 'foo')[to]()).toEqual(expected);
    });

    testEncode('toHex', '0x666f6f');
    testEncode('toString', 'foo');
    testEncode('toU8a', Uint8Array.from([12, 102, 111, 111]));
  });
  describe('utils', () => {
    it('compares actual string values', () => {
      expect(new _.Text(registry, '123').eq('123')).toBe(true);
    });
    it('compares actual String values', () => {
      expect(new _.Text(registry, 'XYX').eq(String('XYX'))).toBe(true);
    });
    it('compares actual non-string values (fails)', () => {
      expect(new _.Text(registry, '123').eq(123)).toBe(false);
    });
    it('calulates the length & encoded length correctly for ASCII', () => {
      const test = new _.Text(registry, 'abcde');
      expect(test.encodedLength).toEqual(6);
      expect(test).toHaveLength(5);
    });
    it('calulates the length & encoded length correctly for non-ASCII', () => {
      const test = new _.Text(registry, '中文');
      expect(test.encodedLength).toEqual(7);
      expect(test).toHaveLength(2);
    });
  });
});