"use strict";

var _util = require("@polkadot/util");

var _addressToEvm = require("./addressToEvm");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('addressToEvm', () => {
  it('creates a valid known EVM address', () => {
    expect((0, _addressToEvm.addressToEvm)('KWCv1L3QX9LDPwY4VzvLmarEmXjVJidUzZcinvVnmxAJJCBou')).toEqual((0, _util.hexToU8a)('0x03b9dc646dd71118e5f7fda681ad9eca36eb3ee9'));
  });
});