"use strict";

var _validate = require("./validate");

var _ = require("..");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('mnemonicValidate', () => {
  beforeEach(async () => {
    await (0, _.cryptoWaitReady)();
  });
  it('returns true on valid', () => {
    expect((0, _validate.mnemonicValidate)('seed sock milk update focus rotate barely fade car face mechanic mercy')).toEqual(true);
    expect((0, _validate.mnemonicValidate)('seed sock milk update focus rotate barely fade car face mechanic mercy', true)).toEqual(true);
  });
  it('returns false on invalid', () => {
    expect((0, _validate.mnemonicValidate)('wine photo extra cushion basket dwarf humor cloud truck job boat submit')).toEqual(false);
    expect((0, _validate.mnemonicValidate)('wine photo extra cushion basket dwarf humor cloud truck job boat submit', true)).toEqual(false);
  });
});