"use strict";

var _util = require("@polkadot/util");

var _asU8a = require("../random/asU8a");

var _hasher = require("./hasher");

var _ = require(".");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
const MESSAGE = (0, _util.stringToU8a)('this is a message');
describe('sign and verify', () => {
  it('verify message signature', () => {
    const address = '0x59f587c045d4d4e9aa1016eae43770fc0551df8a385027723342753a876aeef0';
    const sig = '0x92fcacf0946bbd10b31dfe16d567ed1d3014e81007dd9e5256e19c0f07eacc1643b151ca29e449a765e16a7ce59b88d800467d6b3412d30ea8ad22307a59664b00';
    const msg = (0, _util.stringToU8a)('secp256k1');
    expect((0, _.secp256k1Verify)(msg, sig, address)).toBe(true);
  });
  it('has 65-byte signatures', () => {
    const pair = (0, _.secp256k1KeypairFromSeed)((0, _asU8a.randomAsU8a)());
    expect((0, _.secp256k1Sign)(MESSAGE, pair)).toHaveLength(65);
  });
  it('signs/verifies a message by random key (blake2)', () => {
    const pair = (0, _.secp256k1KeypairFromSeed)((0, _asU8a.randomAsU8a)());
    const signature = (0, _.secp256k1Sign)(MESSAGE, pair);
    const address = (0, _hasher.secp256k1Hasher)('blake2', pair.publicKey);
    expect((0, _.secp256k1Verify)(MESSAGE, signature, address)).toBe(true);
  });
  it('signs/verifies a message by random key (keccak)', () => {
    const pair = (0, _.secp256k1KeypairFromSeed)((0, _asU8a.randomAsU8a)());
    const signature = (0, _.secp256k1Sign)(MESSAGE, pair, 'keccak');
    const address = (0, _hasher.secp256k1Hasher)('keccak', (0, _.secp256k1Expand)(pair.publicKey));
    expect((0, _.secp256k1Verify)(MESSAGE, signature, address, 'keccak')).toBe(true);
  });
  it('fails verification on hasher mismatches', () => {
    const pair = (0, _.secp256k1KeypairFromSeed)((0, _asU8a.randomAsU8a)());
    const signature = (0, _.secp256k1Sign)(MESSAGE, pair, 'keccak');
    const address = (0, _hasher.secp256k1Hasher)('keccak', (0, _.secp256k1Expand)(pair.publicKey));
    expect((0, _.secp256k1Verify)(MESSAGE, signature, address, 'blake2')).toBe(false);
  });
  it('works over a range of random keys (blake2)', () => {
    for (let i = 0; i < 256; i++) {
      const pair = (0, _.secp256k1KeypairFromSeed)((0, _asU8a.randomAsU8a)());

      try {
        expect((0, _.secp256k1Verify)(MESSAGE, (0, _.secp256k1Sign)(MESSAGE, pair, 'blake2'), (0, _hasher.secp256k1Hasher)('blake2', pair.publicKey), 'blake2')).toBe(true);
      } catch (error) {
        console.error(`blake2 failed on #${i}`);
        throw error;
      }
    }
  }, 120000);
  it('works over a range of random keys (keccak)', () => {
    for (let i = 0; i < 256; i++) {
      const pair = (0, _.secp256k1KeypairFromSeed)((0, _asU8a.randomAsU8a)());

      try {
        expect((0, _.secp256k1Verify)(MESSAGE, (0, _.secp256k1Sign)(MESSAGE, pair, 'keccak'), (0, _hasher.secp256k1Hasher)('keccak', (0, _.secp256k1Expand)(pair.publicKey)), 'keccak')).toBe(true);
      } catch (error) {
        console.error(`keccak failed on #${i}`);
        throw error;
      }
    }
  }, 120000);
});