"use strict";

var _ = require(".");

// Copyright 2017-2020 @polkadot/util authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('formatDecimal', () => {
  it('formats decimals in number groupings', () => {
    expect((0, _.formatDecimal)('12345')).toEqual('12,345');
  });
  it('formats decimal-only in number groupings', () => {
    expect((0, _.formatDecimal)('test6789')).toEqual('6,789');
  });
  it('returns input for non-decimal', () => {
    expect((0, _.formatDecimal)('test')).toEqual('test');
  });
  it('returns non-sensical negative text', () => {
    expect((0, _.formatDecimal)('-test')).toEqual('-test');
  });
  it('formats negative numbers', () => {
    expect((0, _.formatDecimal)('-123456')).toEqual('-123,456');
  });
});