"use strict";

var _interopRequireDefault = require("@babel/runtime/helpers/interopRequireDefault");

var _bn = _interopRequireDefault(require("bn.js"));

var _ = require(".");

// Copyright 2017-2020 @polkadot/util authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('formatNumber', () => {
  it('formats empty', () => {
    expect((0, _.formatNumber)()).toEqual('0');
  });
  it('formats negative numbers', () => {
    expect((0, _.formatNumber)(-123456)).toEqual('-123,456');
  });
  it('formats BN numbers', () => {
    expect((0, _.formatNumber)(new _bn.default(12345))).toEqual('12,345');
  });
  it('formats BigInt numbers', () => {
    expect((0, _.formatNumber)(123456789n)).toEqual('123,456,789');
  });
  it('formats Compact<BN>', () => {
    expect((0, _.formatNumber)({
      toBn: () => new _bn.default(12345),
      unwrap: () => new _bn.default(0)
    })).toEqual('12,345');
  });
});